/*-------------------------------------------------------------------------------------------------+
>>  대진일 Dataset의 경주수 Column의 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function dsOutCfrntDay_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID != "RACE_CNT" ) return;

    var objDataSet = dsOutCfrntRace;
    objDataSet.Filter("DAY_ORD=" + obj.GetColumn(nRow, "DAY_ORD"));
	var sRaceNo = "";
	nRowCount = objDataSet.GetRowCount();

	if ( nRowCount < toInteger(varNewValue) ) {
		for ( var i = nRowCount; i < toInteger(varNewValue); i++ ) {
			objDataSet.AddRow();

			if ( i + 1 < 10 ) {
				sRaceNo = "0" + toInteger(i + 1);
			} else {
				sRaceNo = toInteger(i + 1);
			}

			objDataSet.SetColumn(objDataSet.currow, Trim("CFRNT_CD       "), dsOutCfrntDay.GetColumn(obj.currow, "CFRNT_CD"));
			objDataSet.SetColumn(objDataSet.currow, Trim("DAY_ORD        "), dsOutCfrntDay.GetColumn(obj.currow, "DAY_ORD"));
			objDataSet.SetColumn(objDataSet.currow, Trim("RACE_NO        "), sRaceNo);
			objDataSet.SetColumn(objDataSet.currow, Trim("RACE_DGRE_CD   "), "101"  );
			objDataSet.SetColumn(objDataSet.currow, Trim("ST_MTHD_CD     "), "002"  );
			objDataSet.SetColumn(objDataSet.currow, Trim("STRT_TIME      "), GetRaceTime(sRaceNo));
			objDataSet.SetColumn(objDataSet.currow, Trim("RACE_REG_NO_CNT"), 6      );
			objDataSet.SetColumn(objDataSet.currow, Trim("RACE_DIST      "), 1800   );
			objDataSet.SetColumn(objDataSet.currow, Trim("TURN_CNT       "), 3      );
		}
	} else {
		for ( var i = nRowCount; i >= toInteger(varNewValue); i-- ) {
			objDataSet.DeleteRow(i);
		}
	}

    objDataSet.UnFilter();
    dsOutCfrntRace.Sort("DAY_ORD,RACE_NO", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  경주추가
+-------------------------------------------------------------------------------------------------*/
function GetRaceTime(sRaceNo) {
    var sRaceTime = Array(15);

    var i = 0;
    sRaceTime[i++] = "1200";
    sRaceTime[i++] = "1225";
    sRaceTime[i++] = "1250";
    sRaceTime[i++] = "1320";
    sRaceTime[i++] = "1350";
    sRaceTime[i++] = "1420";
    sRaceTime[i++] = "1450";
    sRaceTime[i++] = "1520";
    sRaceTime[i++] = "1550";
    sRaceTime[i++] = "1620";
    sRaceTime[i++] = "1650";
    sRaceTime[i++] = "1720";
    sRaceTime[i++] = "1750";
    sRaceTime[i++] = "1820";
    sRaceTime[i++] = "1850";

    return sRaceTime[toInteger(sRaceNo) - 1];
}

/*-------------------------------------------------------------------------------------------------+
>>  대진코드 Column의 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function dsOutCfrntMeth_OnRowPosChanged(obj,nOldRow,nRow)
{
    if ( dsOutCfrntMeth.GetRowCount() == 0 ) return;

    if ( dsOutCfrntMeth.GetRowCount() <= dsOutCfrntMeth.currow ) {
        dsOutCfrntMeth.FireEvent = false;
        dsOutCfrntMeth.row = dsOutCfrntMeth.GetRowCount() - 1;
        dsOutCfrntMeth.SelectRow(dsOutCfrntMeth.GetRowCount() - 1);
        dsOutCfrntMeth.FireEvent = true;
    }

	if ( fnc_DatasetChangeCheck(dsOutRacerArrangeStnd) 
	  || fnc_DatasetChangeCheck(dsOutCfrntDay) 
	  || fnc_DatasetChangeCheck(dsOutCfrntRace) 
	  ) {
		if ( fnc_Message(SNIS_COM_1002, "Y") ) {
            dsOutCfrntMeth.FireEvent = false;
            dsOutCfrntMeth.row = nOldRow;
			//fcSave();
            dsOutCfrntMeth.FireEvent = true;
			return;
		}
	}

    fcInit();
    if ( fnc_IsNull(dsOutCfrntMeth.GetColumn(nRow, "CFRNT_CD")) ) return;

	fcSearchDetail();
}

/*-------------------------------------------------------------------------------------------------+
>>  대진코드 선택시
+-------------------------------------------------------------------------------------------------*/
function grdCfrntMeth_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sCfrntCd = dsOutCfrntMeth.GetColumn(nRow, "CFRNT_CD");

	if ( fnc_IsNull(sCfrntCd) ) return;

    // Content로 넘겨지는 arguements
	var sArg = "Arg_sCfrntCd=" + quote(sCfrntCd);

    if ( Dialog("form::E02010021.xml", sArg, 834, 450, "") ) fcSearch(divSearch.btnSearch);
}
