/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    if ( obj.id == "btnCancel" || obj.id == "btnSearch" || obj.id == "cboSStndYear" || obj.id == "cboSMbr" ) {
		dsOutRaceTms.ClearData();
	}
    dsOutRacerArrangeStnd.ClearData();
    dsOutRaceDayOrd.ClearData();
    dsOutRace.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e02010030-service:searchRaceTms";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRaceTms=dsOutRaceTms ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  경주 상세 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchDetailCheck() {
    if ( dsOutRaceTms.GetRowCount() == 0 ) return false;

	fcInit();

	return true;
}

function fcSearchDetail(obj) {
    if ( !fcSearchDetailCheck() ) return;

	var sServiceName = "e02010030-service:searchRacerArrangeStnd";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerArrangeStnd=dsOutRacerArrangeStnd "
                     + "dsOutRaceDayOrd=dsOutRaceDayOrd "
                     + "dsOutRace=dsOutRace ";

	fce_AddParam(Trim("STND_YEAR"), dsOutRaceTms.GetColumn(dsOutRaceTms.currow, Trim("STND_YEAR")));
	fce_AddParam(Trim("MBR_CD   "), dsOutRaceTms.GetColumn(dsOutRaceTms.currow, Trim("MBR_CD   ")));
	fce_AddParam(Trim("TMS      "), dsOutRaceTms.GetColumn(dsOutRaceTms.currow, Trim("TMS      ")));
	fce_AddParam(Trim("CFRNT_CD "), dsOutRaceTms.GetColumn(dsOutRaceTms.currow, Trim("CFRNT_CD ")));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  대진코드별 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchCfrntCheck() {
    if ( fnc_IsNull(edCfrntCd.text) ) return false;

	fcInit();

	return true;
}

function fcSearchCfrnt(obj) {
    if ( !fcSearchCfrntCheck() ) return;

	var sServiceName = "e02010020-service:searchRacerArrangeStnd";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerArrangeStnd=dsOutRacerArrangeStnd "
                     + "dsOutRaceDayOrd=dsOutCfrntDay "
