	fce_AddParam(Trim("CFRNT_CD "), dsOutRaceTms.GetColumn(dsOutRaceTms.currow, Trim("CFRNT_CD")));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRaceTms) && !fnc_DatasetChangeCheck(dsOutRaceDayOrd) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutRaceTms;
	sMandatoryColID   = "STND_YEAR|TMS|QURT_CD|MONTH|STR_DT|END_DT|CFRNT_CD|MBR_CD|ARRANGE_TPE_CD";
	sMandatoryColName = "기준년도|회차|분기|월|시작일자|종료일자|대진코드|경정장코드|주선종류";
	oGrid             = grdRaceTms;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
/*	
	oDataSet          = dsOutRaceDayOrd;
	sMandatoryColID   = "RACE_ORD|RACE_CNT|RACE_DAY";
	sMandatoryColName = "대진일차|경주수|대진일";
	oGrid             = grdRaceDay;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
*/
	// 기간이 2일보다 큰 경우 확인 메세지를 띄움(날짜 차이는 1일)
	var nTerm = ParseDateTime(calEndDt.value) - ParseDateTime(calStrDt.value);
	if (nTerm > 1) {
		if (!fnc_message("기간이 " + toString(nTerm + 1) + "일입니다. 맞나요?", "Y")) {
			return false;
		}
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e02010030-service:saveRaceTms";
    var sInDataSet   = "dsOutRaceTms=dsOutRaceTms:u "
                     + "dsOutRaceDayOrd=dsOutRaceDayOrd:u ";
    var sOutDataSet  = "dsOutRaceTms=dsOutRaceTms ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("SAVE_TMS "), dsOutRaceTms.GetColumn(dsOutRaceTms.row, "TMS"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

