/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 
	switch ( obj.GetForm().id ) {
		case "divBtnRowRaceTms" :
            // if ( fnc_DatasetChangeCheck(dsOutRaceTms) ) {
                // if ( fnc_Message(SNIS_COM_1002, 'Y') ) return;
            // }

            fcInit();
			dsOutRaceTms.AddRow();         //Record Add

			dsOutRaceTms.SetColumn(dsOutRaceTms.currow, "STND_YEAR", cboSStndYear.Value    );
			dsOutRaceTms.SetColumn(dsOutRaceTms.currow, "MBR_CD"   , cboSMbr.Value         );
			dsOutRaceTms.SetColumn(dsOutRaceTms.currow, "MONTH"    , fce_GetSysdate("MM"  ));

			grdRaceTms.SetFocus();         //입력 첫 항목으로 Focus
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	switch ( obj.GetForm().id ) {
		case "divBtnRowRaceTms" :
			if ( !fnc_DeleteCheck(dsOutRaceTms, "chk", "1") ) return;
            fcInit();
			grdRaceTms.SetFocus();                                  //Record 삭제 후 Grid로 Focus
			dsOutRaceTms_OnRowPosChanged();
			break;
	}
}

