/*-------------------------------------------------------------------------------------------------+
>>  그리드에서 팝업 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnExpandEdit(objGrd, nRow, nCell, strVal, nPivotIndex){
	var sColIdx     = objGrd.GetCellText("HEAD", nRow, nCell);  //해당 HEAD명 
	switch (sColIdx){
		case "대진일" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;
		default :
			break;
	}
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Value = fce_GetUserInfo("MBR_CD");
		fce_AddCodeRow(dsOutRacerGrd, "전체", "00");
		fcSearch();
		return;
	}

	// 조회 후
	if ( sServiceName == "e02010030-service:searchRaceTms" ) {
        dsOutRaceTms.AddColumn("chk");
        fnc_SetStatus(SNIS_BOA_0003, dsOutRaceTms.GetRowCount());
		return;
	}

	// 조회 후
	if ( sServiceName == "e02010030-service:searchRacerArrangeStnd" ) {
        dsOutRacerArrangeStnd.AddColumn("chk");
        dsOutRaceDayOrd.Sort("DAY_ORD",True);
		return;
	}

	// 조회 후
	if ( sServiceName == "e02010020-service:searchRacerArrangeStnd" ) {
        dsOutRaceDayOrd.AddColumn("RACE_DAY");

        var sStrDt = dsOutRaceTms.GetColumn(dsOutRaceTms.currow, "STR_DT");
        if ( fnc_IsNull(sStrDt) ) return;
        for ( var i = 0; i < dsOutRaceDayOrd.GetRowCount(); i++ ) {
            dsOutRaceDayOrd.SetColumn(i, "RACE_DAY", AddDate(sStrDt, i));
        }

		dsOutRaceDayOrd.Sort("DAY_ORD",True);
        return;
	}

	// 저장 후
	if ( sServiceName == "e02010030-service:saveRaceTms" ) {
        dsOutRaceTms.AddColumn("chk");
		return;
	}

	//
	if( sServiceName == "common_race-service:searchRaceDayOrd" ){		
		//dsOutRaceTms.GetColumn(dsOutRaceTms.row, "PRIZ_DAY_ORD");
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

