/*-------------------------------------------------------------------------------------------------+
>>  경주회차 변경시
+-------------------------------------------------------------------------------------------------*/
function dsOutRaceTms_OnRowPosChanged(obj,nOldRow,nRow)
{
	if ( dsOutRaceTms.GetRowType(nRow) == "insert" ) {
        mskTms.Readonly = false;
        fnc_SetBKColor(this);

        fcSearchCfrnt();
	} else {
        mskTms.Readonly = true;
        fnc_SetBKColor(this);
        fcSearchDetail();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  회차 입력시
+-------------------------------------------------------------------------------------------------*/
function dsOutRaceTms_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if ( strColumnID == "TMS" ) {

	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "회차가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "회차가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }

        return;
    }

	if ( strColumnID == "STR_DT" ) {

        for ( var i = 0; i < dsOutRaceDayOrd.GetRowCount(); i++ ) {
            dsOutRaceDayOrd.SetColumn(i, "RACE_DAY", AddDate(varNewValue, i));
        }

        return;
    }
}

function cbPrizdayOrd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	filterDsPrizRaceNo();
}


function dsOutRace_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	filterDsPrizRaceNo();
}

function filterDsPrizRaceNo(){
	dsOutPrizRaceNo.Copy("dsOutRace");
	dsOutPrizRaceNo.Filter("DAY_ORD = '" + cbPrizdayOrd.Value + "'");
	dsOutPrizRaceNo.Sort("RACE_NO",True); 
}

function btnPrizClear_OnClick(obj)
{
	if(Confirm("대상경주 데이터를 지우시겠습니까?\n\n(저장을 누르셔야 최종 적용됩니다.)")){
		cbPrizRaceGrpCd.Value = "";
		cbPrizdayOrd.Value = "";
		cbPrizRaceNo.Value = "";
	}
}


function Button0_OnClick(obj)
{
	var nTerm = ParseDateTime(calEndDt.value) - ParseDateTime(calStrDt.value);
	alert(nTerm);
	if (nTerm > 1) {
		if (!fnc_message("기간이 " + toString(nTerm + 1) + "일입니다. 맞나요?", "Y")) return ;
	}
	alert("OK");
}
