/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// °øÅëÄÚµå Á¶È¸ ¿Ï·á ÈÄ
	if ( sServiceName == "common-service:searchCode" ) {                        
        cboEndPerio.Index = dsOutRacerPerioNo.GetRowCount() - 1;
        fcRacerCondProcess();
		fcSearchRecdStndTerm();
		cboSQurt.Index = 0;
		dsOutRacerGrd.Copy(dsOutSRacerGrd);
        fce_AddCodeRow(dsOutSRacerGrd, "ÀüÃ¼", "", cboSRacerGrd, false);

        return;
	}

	// Á¶È¸ ÈÄ
	if ( sServiceName == "e02010040-service:searchRecdStndTerm" ) {
        cboSQurt_OnChanged();
        return;
    }

	// Á¶È¸ ÈÄ
	if ( sServiceName == "e02020020-service:searchRacerGrdRaceCnt" ) {
        fcSetRaceCnt();
        fcSetRacerCnt();
        return;
    }

	// Á¶È¸ ÈÄ
	if ( sServiceName == "e02020020-service:searchRacerDegree" ) {
        return;

    }
    if ( sServiceName == "e02020030-service:searchRacerRankDetail" ) {
        fcSetExpression();
        return;
    }

	// ÀúÀå ÈÄ
	if ( sServiceName == "e02020020-service:saveRacerDegree" ) {
        return;
    }

	//¿À·ù°¡ ¾øÀÌ Á¤»ó Ã³¸®µÇ¾ú´Ù¸é...
	fcPostProcess(sServiceName);
}

