/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutRaceInfo.ClearData();
    dsOutStndRaceCnt.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e02030010-service:searchStndRaceCnt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutStndRaceCnt=dsOutStndRaceCnt "
                     + "dsOutRaceInfo=dsOutRaceInfo ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("QURT_CD"  ), cboSQurt.Value    );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutStndRaceCnt       ) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutStndRaceCnt;
	sMandatoryColID   = "STND_RACE_CNT";
	sMandatoryColName = "기준출주횟수";
	oGrid             = grdStndRaceCnt;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e02030010-service:saveStndRaceCnt";
    var sInDataSet   = "dsOutStndRaceCnt=dsOutStndRaceCnt ";
    var sOutDataSet  = "dsOutStndRaceCnt=dsOutStndRaceCnt "
                     + "dsOutRaceInfo=dsOutRaceInfo ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("QURT_CD"  ), cboSQurt.Value    );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

