/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboSQurt.Index = 0;
		fcSearch();
		return;
	}

	// 조회 후
	if ( sServiceName == "e02030010-service:searchStndRaceCnt" ) {
        fnc_SetStatus(SNIS_BOA_0003, dsOutStndRaceCnt.GetRowCount());
	    if ( dsOutRaceInfo.GetColumn(dsOutRaceInfo.currow, "RACER_CNT") == 0 ) {
            return;
	    }

        dsOutRaceInfo_OnColumnChanged();
		return;
	}

	// 저장 후
	if ( sServiceName == "e02030010-service:saveStndRaceCnt" ) {
        dsOutRaceInfo_OnColumnChanged();
		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

