/*-------------------------------------------------------------------------------------------------+
>>  계산번튼 클릭시 기준출주횟수 계산
+-------------------------------------------------------------------------------------------------*/
function btnCompute_OnClick(obj)
{
    if ( dsOutStndRaceCnt.GetRowCount() == 0 ) return;
	var nStndRaceCnt = toInteger(mskStndRaceCnt.Value);

	for ( var i = 0; i < dsOutStndRaceCnt.GetRowCount(); i++ ) {
        var nRaceCnt = Round(nStndRaceCnt * toInteger(dsOutStndRaceCnt.GetColumn(i, "RACE_RT")) / 80);
        dsOutStndRaceCnt.SetColumn(i, "STND_RACE_CNT", nRaceCnt);
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  경주정보변경시
+-------------------------------------------------------------------------------------------------*/
function dsOutRaceInfo_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
    if ( strColumnID != "TOT_RACE_CNT" ) {
        var sTmsCnt     = dsOutRaceInfo.GetColumn(dsOutRaceInfo.currow, "TMS_CNT"     );
        var sDayOrdCnt  = dsOutRaceInfo.GetColumn(dsOutRaceInfo.currow, "DAY_ORD_CNT" );
        var sRaceCnt    = dsOutRaceInfo.GetColumn(dsOutRaceInfo.currow, "RACE_CNT"    );
        var sRaceRegNo  = dsOutRaceInfo.GetColumn(dsOutRaceInfo.currow, "RACE_REG_NO" );
        var sTotRaceCnt = dsOutRaceInfo.GetColumn(dsOutRaceInfo.currow, "TOT_RACE_CNT");
        var sRacerCnt   = dsOutRaceInfo.GetColumn(dsOutRaceInfo.currow, "RACER_CNT"   );

        sTotRaceCnt = toInteger(sTmsCnt) * toInteger(sDayOrdCnt) * toInteger(sRaceCnt) * toInteger(sRaceRegNo);

        dsOutRaceInfo.FireEvent = false;
        dsOutRaceInfo.SetColumn(dsOutRaceInfo.currow, "TMS_CNT"     , sTmsCNT    );
        dsOutRaceInfo.SetColumn(dsOutRaceInfo.currow, "DAY_ORD_CNT" , sDayOrdCnt );
        dsOutRaceInfo.SetColumn(dsOutRaceInfo.currow, "RACE_CNT"    , sRaceCnt   );
        dsOutRaceInfo.SetColumn(dsOutRaceInfo.currow, "RACE_REG_NO" , sRaceRegNo );
        dsOutRaceInfo.SetColumn(dsOutRaceInfo.currow, "TOT_RACE_CNT", sTotRaceCnt);
        dsOutRaceInfo.SetColumn(dsOutRaceInfo.currow, "RACER_CNT"   , sRacerCnt  );
        dsOutRaceInfo.FireEvent = true;

        if ( sRacerCnt == 0 ) return;
        mskStndRaceCnt.Value = round(toInteger(sTotRaceCnt) / toInteger(sRacerCnt));
    } else {
        var sTotRaceCnt = dsOutRaceInfo.GetColumn(dsOutRaceInfo.currow, "TOT_RACE_CNT");
        var sRacerCnt   = dsOutRaceInfo.GetColumn(dsOutRaceInfo.currow, "RACER_CNT"   );

        if ( sRacerCnt == 0 ) return;
        mskStndRaceCnt.Value = round(toInteger(sTotRaceCnt) / toInteger(sRacerCnt));
    }
}
