// Á¶È¸¹öÆ° ÀÌº¥Æ®
function fcSearch(obj) {
    var sServiceName = ""; 
    var sInDataSet   = "";
    var sOutDataSet  = "";

	sServiceName = "e02040040-service:searchRacerpopup";
	sInDataSet   = "";
	sOutDataSet  = "dsRacerResult=dsRacerResult dsMotorResult=dsMotorResult dsBoatResult=dsBoatResult";

    fce_AddParam(Trim("STND_YEAR"), 			Arg_sStndYear		); //³âµµ
	fce_AddParam(Trim("MBR_CD"), 				Arg_sMbrcd     		); //°æÁ¤ÀåÄÚµå
//	fce_AddParam(Trim("TMS_DAYORD"), 			Arg_sTms + Arg_sDayOrd); //È¸Â÷ ÀÏÂ÷
	fce_AddParam(Trim("RACER_NO"), 				Arg_sRacerNo 		); //¼±¼ö¹øÈ£
	fce_AddParam(Trim("MOTOR_NO"), 				Arg_sMotorNo 		); //¸ðÅÍ¹øÈ£
	fce_AddParam(Trim("BOAT_NO"), 				Arg_sBoatNo 		); //º¸Æ®¹øÈ£
	fce_AddParam(Trim("ST_MTHD_CD"), 			cboStMthdCd.value 	); //¿Â¶óÀÎ½ºÅ¸Æ®¿©ºÎ

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


// CallBackÇÔ¼ö
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// Á¶È¸ ÈÄ 
	if ( sServiceName == "e02040040-service:searchRacerpopup" ) {
		//±×¸®µå HeadÀÇ TEXT°ª º¯°æ
		grdRacerResult.SetCellProp("head", 0, "text", "¼± ¼ö : " + Arg_sRacerNm + "(" + Arg_sRacerNo + ")") ;
		grdMotorResult.SetCellProp("head", 0, "text", "¸ð ÅÍ : " + Arg_sMotorNo) ;
		grdBoatResult.SetCellProp("head", 0, "text", "º¸ Æ® : " + Arg_sBoatNo) ;
		return;
	}	
}


