/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        fcSearch();
		return;
	}

	// 조회 후
	if ( sServiceName == "e02040042-service:searchRacerRelation" ) {
	    if(dsOutRacerRelation.rowcount <= 0) {
            fnc_Message(L_COM_ALT_0024, "N", "조회된 레코드가 ");
        }

        if(!fnc_IsNull(trim(Arg_sRacerNo7)) && fnc_IsNull(trim(Arg_sRacerNo8))){

			window.height = 216;
			grdRacer.Height = 147;

        }else if (!fnc_IsNull(trim(Arg_sRacerNo8))){ 

        	window.height = 235;
			grdRacer.Height = 166;

		} 


		var sBkColorRace = "expr:iif(dsOutRacerRelation.GetColumn(row,&apos;RACE_REG_NO&apos;)==2,&quot;#000000&quot;"
                         + ",iif(dsOutRacerRelation.GetColumn(row,&apos;RACE_REG_NO&apos;)==3,&quot;#FF0000&quot;"
                         + ",iif(dsOutRacerRelation.GetColumn(row,&apos;RACE_REG_NO&apos;)==4,&quot;#0000FF&quot;"
                         + ",iif(dsOutRacerRelation.GetColumn(row,&apos;RACE_REG_NO&apos;)==5,&quot;#FFFF00&quot;"
                         + ",iif(dsOutRacerRelation.GetColumn(row,&apos;RACE_REG_NO&apos;)==6,&quot;#00FF00&quot;"
                         + ",iif(dsOutRacerRelation.GetColumn(row,&apos;RACE_REG_NO&apos;)==7,&quot;" + GBL_REG_COLOR_7 + "&quot;"
                         + ",iif(dsOutRacerRelation.GetColumn(row,&apos;RACE_REG_NO&apos;)==8,&quot;" + GBL_REG_COLOR_8 + "&quot;"
                         + ",&quot;#FFFFFF&quot;)))))))";
		var sColorRace   = "expr:iif(dsOutRacerRelation.GetColumn(row,&apos;RACE_REG_NO&apos;)==2||dsOutRacerRelation.GetColumn(row,&apos;RACE_REG_NO&apos;)==3||dsOutRacerRelation.GetColumn(row,&apos;RACE_REG_NO&apos;)==4,&quot;#FFFFFF&quot;,&quot;#000000&quot;)";

		var colStr = ' <columns>     '
					    + '<col width="50"/>'
						+ '<col width="29"/>'
						+ '<col width="27"/>'
						+ '<col width="28"/>'
						+ '<col width="56"/>'
						+ '<col width="62"/>'
						+ '<col width="177"/>'
						+ '<col width="184"/>'
						+ '<col width="86"/>'
					+ '</columns>';
		var headStr = '<head>   '
						+ '<cell bkcolor="user20" col="0" color="user21" display="text" font="굴림,9" text="경주번호"/>'
						+ '<cell bkcolor="user20" col="1" color="user21" display="text" font="굴림,9" text="정번"/>'
						+ '<cell bkcolor="user20" col="2" color="user21" display="text" font="굴림,9" text="기수"/>'
						+ '<cell bkcolor="user20" col="3" color="user21" display="text" font="굴림,9" text="색상"/>'
						+ '<cell bkcolor="user20" col="4" color="user21" display="text" font="굴림,9" text="선수번호"/>'
						+ '<cell bkcolor="user20" col="5" color="user21" display="text" font="굴림,9" text="선수명"/>'
						+ '<cell bkcolor="user20" col="6" color="user21" display="text" font="굴림,9" text="전경주"/>'
						+ '<cell bkcolor="user20" col="7" color="user21" display="text" font="굴림,9" text="경주관련선수"/>'
						+ '<cell bkcolor="user20" col="8" color="user21" display="text" font="굴림,9" text="연대관련선수"/>'
					+ '</head>';
		var bodyStr = '<body>  '
						+ '<cell align="center" col="0" colid="RACE_NO" display="text"/>'
						+ '<cell align="center" col="1" colid="RACE_REG_NO" display="combo"/>'
						+ '<cell align="center" col="2" colid="RACER_PERIO_NO" display="text"/>'
						+ '<cell align="center" col="3" colid="CD_NM" display="text" bkColor="' + sBkColorRace + '" color="' + sColorRace + '"/>  ' 
						+ '<cell align="center" col="4" colid="RACER_NO" display="text"/>'
						+ '<cell align="center" col="5" colid="NM_KOR" display="text"/>'
						+ '<cell align="left" col="6" colid="ESC_RACE" display="text"/>'
						+ '<cell align="left" col="7" colid="VERIFI_RACER" display="text"/>'
						+ '<cell align="left" col="8" colid="RELA_RACER" display="text"/>'
					+ '</body>';		

		grdRacer.Contents = "<contents>" + colStr + headStr + bodyStr + "</contents>";

        return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

