/*-------------------------------------------------------------------------------------------------+
>>  Grid 생성
+-------------------------------------------------------------------------------------------------*/
function fcCreateGrid() {
    Destroy("grdRacerVerify");

    var sContents = "";
    sContents = '                  <contents>';
    sContents = sContents + fcGetColumn() + fcGetHead() + fcGetBody() ; //+ fcGetSummary();
    sContents = sContents + '      </contents>';


    sGridType   = 'AutoFit="TRUE" '
                + 'AutoFitEndLine="Hide" '
                + 'BindDataset="dsOutRaceVerify" '
                + 'BKColor="user15" '
                + 'BkColor2="user16" '
                + 'BkSelColor="user17" '
                + 'BoldHead="true" '
                + 'Border="Flat" '
                + 'BorderColor="user22" '
                + 'Bottom="579" '
                + 'ColSizing="True" '
                + 'Enable="true" '
                + 'EndLineColor="default" '
                + 'FillArea="TRUE" '
                + 'HeadBorder="Flat" '
                + 'HeadHeight="20" '
                + 'Height="491" '
                + 'InputPanel="FALSE" '
                + 'Left="6" '
                + 'LineColor="user18" '
                + 'MinWidth="100" '
                + 'OnHeadClick="fcGridSort" '
                + 'Right="1264" '
                + 'SelColor="user19" '
                + 'Style="snis_grid" '
                + 'TabOrder="8" '
                + 'TabStop="true" '
                + 'Top="88" '
                + 'UseDBuff="true" '
                + 'UsePopupMenu="true" '
                + 'UseSelColor="true" '
                + 'Visible="true" '
                + 'VLineColor="default" '
                + 'WheelScrollRow="10" '
                + 'Width="1258" ';

    create("Grid","grdRacerVerify", sGridType);
	grdRacerVerify.contents = sContents;
}

