            + '                     	<col width="55"/>  '
            + '                     	<col width="32"/>  '
            + '                     	<col width="35"/>  '
            + '                     	<col width="64"/>  '
            + '                     	<col width="67"/>  '
            + '                     	<col width="144"/> '
            + '                     	<col width="220"/> '
            + '                     	<col width="105"/> '
            + '                     	<col width="40"/>  '
            + '                     	<col width="40"/>  '
            + '                     	<col width="40"/>  '
            + '                     	<col width="40"/>  '
            + '                     	<col width="40"/>  '
            + '                     	<col width="40"/>  ';

    for ( var i = 0; i < dsOutRacerPerioNo.GetRowCount(); i++ ) {
        sColumn += '                    <col width="40"/>  ';
