/*-------------------------------------------------------------------------------------------------+
>>  Grid Body 생성
+-------------------------------------------------------------------------------------------------*/
function fcGetBody() {
    var sBody        = "";
    var sBkColor     = "expr:iif(dsOutRaceVerify.GetColumn(row,&apos;REG_CNT&apos;)&gt;1,&apos;#957745&apos;,&apos;&apos;)";
    var sBkColorRace = "expr:iif(dsOutRaceVerify.GetColumn(row,&apos;RACE_REG_NO&apos;)==2,&quot;#000000&quot;"
                         + ",iif(dsOutRaceVerify.GetColumn(row,&apos;RACE_REG_NO&apos;)==3,&quot;#FF0000&quot;"
                         + ",iif(dsOutRaceVerify.GetColumn(row,&apos;RACE_REG_NO&apos;)==4,&quot;#0000FF&quot;"
                         + ",iif(dsOutRaceVerify.GetColumn(row,&apos;RACE_REG_NO&apos;)==5,&quot;#FFFF00&quot;"
                         + ",iif(dsOutRaceVerify.GetColumn(row,&apos;RACE_REG_NO&apos;)==6,&quot;#00FF00&quot;"
                         + ",iif(dsOutRaceVerify.GetColumn(row,&apos;RACE_REG_NO&apos;)==7,&quot;" + GBL_REG_COLOR_7 + "&quot;"
                         + ",iif(dsOutRaceVerify.GetColumn(row,&apos;RACE_REG_NO&apos;)==8,&quot;" + GBL_REG_COLOR_8 + "&quot;"
                         + ",&quot;#FFFFFF&quot;)))))))";
    var sColorRace   = "expr:iif(dsOutRaceVerify.GetColumn(row,&apos;RACE_REG_NO&apos;)==2||dsOutRaceVerify.GetColumn(row,&apos;RACE_REG_NO&apos;)==3||dsOutRaceVerify.GetColumn(row,&apos;RACE_REG_NO&apos;)==4,&quot;#FFFFFF&quot;,&quot;#000000&quot;)";

        sBody       = '     <body>  ' 
                    + '     <cell align="center" col="0"  colid="RACE_NO"       display="text" suppress="1"/>  ' 
                    + '     <cell align="center" col="1"  colid="RACE_REG_NO"   display="text" bkcolor="' + sBkColor     + '" />  ' 
                    + '     <cell align="center" col="2"  colid="CD_NM"         display="text" bkColor="' + sBkColorRace + '" color="' + sColorRace + '"/>  ' 
                    + '     <cell align="center" col="3"  colid="RACER_NO"      display="text"/>  ' 
                    + '     <cell align="center" col="4"  colid="NM_KOR"        display="text"/>  ' 
                    + '     <cell align="left"   col="5"  colid="ESC_RACE"      display="text"/>  ' 
                    + '     <cell align="left"   col="6"  colid="VERIFI_RACER"  display="text"/>  ' 
                    + '     <cell align="left"   col="7"  colid="RELA_RACER"    display="text"/>  ' 
                    + '     <cell align="center" col="8"  colid="RACE_CNT_1"    display="text" bkcolor="#BCDCDC" />  ' 
                    + '     <cell align="center" col="9"  colid="RACE_CNT_2"    display="text" bkcolor="#BCDCDC" />  ' 
                    + '     <cell align="center" col="10" colid="RACE_CNT_3"    display="text" bkcolor="#BCDCDC" />  ' 
                    + '     <cell align="center" col="11" colid="RACE_CNT_4"    display="text" bkcolor="#BCDCDC" />  ' 
                    + '     <cell align="center" col="12" colid="RACE_CNT_5"    display="text" bkcolor="#BCDCDC" />  ' 
                    + '     <cell align="center" col="13" colid="RACE_CNT_6"    display="text" bkcolor="#BCDCDC" />  ';

    for ( var i = 0; i < dsOutRacerPerioNo.GetRowCount(); i++ ) {
        var sPerioNo = "SR" + (i + 1);
        sBody      += '    <cell align="center" col="'+ (14 + i) +'" colid="'+ sPerioNo +'" display="text" />';
    }

    sBody   += '</body>';

    return sBody;
}


/*-------------------------------------------------------------------------------------------------+
>>  가편성 목록 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchTempOrganList(obj) {

    var nTmsRow        = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);  // 
    var sState         = dsOutSRaceTms.GetColumn(nTmsRow, "RACE_TMS_STAT_CD");           // 해당 회차 상태

    if ( sState < "020" ) {
		return false;
    }

	var sServiceName = "e02040040-service:searchTempOrgan";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTempOrgan=dsOutTempOrgan ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), cboSDayOrd.Value  );
	fce_AddParam("USER_ID", gdsUser.getColumn(0, "USER_ID"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
