/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    if ( obj != dsOutRace ) {
        dsOutRace      .ClearData();
    }

    dsOutOrgan         .ClearData();
    dsOutRaceEAllotRate.ClearData();
    dsOutRaceTAllotRate.ClearData();
    dsOutRaceTypeMoney .ClearData();
    dsOutMoneyCnt      .ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e02050010-service:searchRace";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRace=dsOutRace ";

	fce_AddParam("STND_YEAR"    , cboSStndYear  .Value);
	fce_AddParam("MBR_CD"       , cboSMbr       .Value);
	fce_AddParam("TMS"          , cboSRaceTms   .Value);
	fce_AddParam("DAY_ORD"      , cboSRaceDayOrd.Value);
	fce_AddParam("MEET_CD"      , "003");
	fce_AddParam(Trim("ST_MTHD_CD"),cboStMthdCd.value 	); //온라인스타트여부

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

