}

function fcGetRaceDetail(obj) {
    if ( !fcGetRaceDetailCheck(obj) ) return;

	var sServiceName = "e02050010-service:searchRaceNoDetail";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrgan=dsOutOrgan ";
        sOutDataSet += "dsOutRaceEAllotRate=dsOutRaceEAllotRate ";
        //sOutDataSet += "dsOutRaceTAllotRate=dsOutRaceTAllotRate ";
        sOutDataSet += "dsOutDivRate=dsOutDivRate ";
        sOutDataSet += "dsOutRaceTypeMoney=dsOutRaceTypeMoney ";
        sOutDataSet += "dsOutMoneyCnt=dsOutMoneyCnt ";

	fce_AddParam("STND_YEAR"    , cboSStndYear  .Value);
	fce_AddParam("MBR_CD"       , cboSMbr       .Value);
	fce_AddParam("TMS"          , cboSRaceTms   .Value);
	fce_AddParam("DAY_ORD"      , cboSRaceDayOrd.Value);
	fce_AddParam("RACE_NO"      , dsOutRace.getColumn(dsOutRace.currow, "RACE_NO"));
	fce_AddParam("MEET_CD"      , "003");
	fce_AddParam(Trim("ST_MTHD_CD"),cboStMthdCd.value 	); //온라인스타트여부

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");    
}


