//IWORKS_2013-12-20 추가.

/*-------------------------------------------------------------------------------------------------+
>>  일차별 최고정번수에 따른 그리드 사이즈 조절 RACE_REG_NO_CNT
+-------------------------------------------------------------------------------------------------*/
function setGridSize(){
	/*
		IWORK_SFR-008 경정 선수편성 메뉴 개선  정번8 까지 추가 수정.
		회차에 따른 최고(8까지)정번수 별로 1 ~ 정번수 까지 화면에 표시.
	*/
	var nMaxTmsRunCnt = 8;
	var nMaxRaceRegNoCnt = dsOutSDayOrd.GetColumn(cboSRaceDayOrd.Index, "MAX_RACE_REG_NO_CNT");

	//Alert("nMaxRaceRegNoCnt : " + nMaxRaceRegNoCnt);

	grdRaceEAllotRate.Redraw = false;

	//전체적으로 사이즈를 넓힘.
	for ( var i = 0; i <= nMaxTmsRunCnt; i++ ) {
        grdRaceEAllotRate.SetColProp(i, "Width", 80);
    }

    //nMaxTmsRunCnt 부터 nMaxRaceRegNoCnt까지 사이즈를 줄임.
    for ( var i = nMaxTmsRunCnt; i > nMaxRaceRegNoCnt; i-- ) {
        grdRaceEAllotRate.SetColProp(i, "Width", 0);
    }

    grdRaceEAllotRate.Height = 20 + nMaxRaceRegNoCnt*18;

    grdRaceEAllotRate.Redraw = true;
}

//DataSet 정상  로드시.
function dsOutDivRate_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	setDsOutDivMap();


	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.

	//각 승식별 Data 분배
	var nRowCount = dsOutDivRate.GetRowCount();
	//alert(":::" + nRowCount);
	//승식
	var sPoolCd;
	//컬럼명
	var sColId;
	//조건식에 맞는 Record Index
	var nSearchRow;
	var nSearchRowMap;
	var oDataSet;
	var sRUNNER_1ST;
	var sRUNNER_2ND;
	var sRUNNER_3RD;
	//배당률
	var sODDS;
	//조건식
	var sSearch;

	dsOutRaceTAllotRate.FireEvent = false;

	for (var i = 0; i < nRowCount; i++)
	{
		sPoolCd		= dsOutDivRate.GetColumn(i, "POOL_CD");
		sRUNNER_1ST = dsOutDivRate.GetColumn(i, "RUNNER_1ST");
		sRUNNER_2ND = dsOutDivRate.GetColumn(i, "RUNNER_2ND");
		sRUNNER_3RD = dsOutDivRate.GetColumn(i, "RUNNER_3RD");
		sODDS		= dsOutDivRate.GetColumn(i, "ODDS");

		sSearch		= "POOL_CD = '" + sPoolCd;
		sSearch    += "' && RUNNER_1ST = '" + sRUNNER_1ST + "' && RUNNER_2ND = '" + sRUNNER_2ND;
		sSearch    += "' && RUNNER_3RD = '" + sRUNNER_3RD + "'";

		nSearchRowMap	= dsOutDivMap.SearchRow(sSearch);

		if (nSearchRowMap > -1)
		{
			oDataSet		= object(dsOutDivMap.GetColumn(nSearchRowMap, "DATASET_ID"));
			nSearchRow		= dsOutDivMap.GetColumn(nSearchRowMap, "GRID_ROW");
			sColId			= dsOutDivMap.GetColumn(nSearchRowMap, "COL_VALUE");

			oDataSet.SetColumn(nSearchRow, sColId, dsOutDivRate.GetColumn(i, "ODDS"));
		}
	}

	dsOutRaceTAllotRate.FireEvent = true;
}


/*-------------------------------------------------------------------------------------------------+
>>  최고정번수에 따른 DsOutDivMap(매핑 데이터셋 변경).
+-------------------------------------------------------------------------------------------------*/
function setDsOutDivMap(){
	var maxRegNoCnt = dsOutSDayOrd.GetColumn(cboSRaceDayOrd.Index, "MAX_RACE_REG_NO_CNT");

	//alert(maxRegNoCnt);

	if(maxRegNoCnt == 7){
		dsOutDivMap.DeleteAll();
		dsOutDivMap.Copy("dsOutDivMap_7");
	}else if(maxRegNoCnt == 8){
		dsOutDivMap.DeleteAll();
		dsOutDivMap.Copy("dsOutDivMap_8");
	}else{
		dsOutDivMap.DeleteAll();
		dsOutDivMap.Copy("dsOutDivMap_6"); // default..
	}

	fcOddsInit();
}

function fcOddsInit(){

    var nRowMap = dsOutDivMap.GetRowCount();
	var nRowIndex;
	var sDataSetID;
	var oDataSet;

	dsOutRaceTAllotRate.FireEvent = false;


	//삼복승초기화
	dsOutRaceTAllotRate.ClearData();
	//맵핑 테이블 정보
	dsOutDivMap.Sort("DATASET_ID,GRID_ROW", true);
	for (var i = 0; i < nRowMap; i++)
	{
		nRowIndex = dsOutDivMap.GetColumn(i, "GRID_ROW");
		sDataSetID = dsOutDivMap.GetColumn(i, "DATASET_ID");
		oDataSet = object(sDataSetID);
		if (oDataSet.GetRowCount() - 1 < nRowIndex)
			oDataSet.AddRow();
		oDataSet.SetColumn(nRowIndex, dsOutDivMap.GetColumn(i, "COL_TEXT"), dsOutDivMap.GetColumn(i, "RACE_REG_NO_TEXT"));
	}


	dsOutRaceTAllotRate.FireEvent = true;
}

