    // «ˆ¿Á »∏¬˜ ¡∂»∏
    fce_GetCurTms();

	//∞¯≈Îƒ⁄µÂ ¡∂»∏
	fce_GetComboDs(dsInComCd);

	// »≠∏È √ ±‚»≠
	//fcInit();
}
