function dsOutRace_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
    var nDayCnt = toInteger(dsOutRace.GetColumn(0, "DAY_CNT"));
    var nWidth  = toInteger((grdPayoffMin.Width - 530) / (nDayCnt * 3));
    var nWidth2  = toInteger((grdHitRace.Width - 500) / (nDayCnt * 3));

    for ( var i = 0; i < nDayCnt; i++ ) {
        grdVoilAct.SetColProp(1 + i, "Width", 60);
        for ( var j = 0; j < 3; j++ ) { 
			grdPayoffMin.SetColProp((j * 14) + (1 + i*2), "Width", nWidth);
			grdPayoffMin.SetColProp((j * 14) + (2 + i*2), "Width", nWidth);
			grdPayoffMax.SetColProp((j * 14) + (1 + i*2), "Width", nWidth);
			grdPayoffMax.SetColProp((j * 14) + (2 + i*2), "Width", nWidth);
			grdPayoffAvg.SetColProp((j * 14) + (1 + i*2), "Width", nWidth);
			grdPayoffAvg.SetColProp((j * 14) + (2 + i*2), "Width", nWidth);
			grdRacePayoff.SetColProp((j * 14) + (1 + i*2), "Width", nWidth);
			grdRacePayoff.SetColProp((j * 14) + (2 + i*2), "Width", nWidth);
			grdHitRace.SetColProp((j * 7) + (2 + i), "Width", nWidth2);
		}
    }
    for ( var i = nDayCnt; i < 7; i++ ) {
        grdVoilAct.SetColProp(1 + i, "Width",  0);
        for ( var j = 0; j < 3; j++ ) { 
			grdPayoffMin.SetColProp((j * 14) + (1 + i*2), "Width", 0);
			grdPayoffMin.SetColProp((j * 14) + (2 + i*2), "Width", 0);
			grdPayoffMax.SetColProp((j * 14) + (1 + i*2), "Width", 0);
			grdPayoffMax.SetColProp((j * 14) + (2 + i*2), "Width", 0);
			grdPayoffAvg.SetColProp((j * 14) + (1 + i*2), "Width", 0);
			grdPayoffAvg.SetColProp((j * 14) + (2 + i*2), "Width", 0);
			grdRacePayoff.SetColProp((j * 14) + (1 + i*2), "Width", 0);
			grdRacePayoff.SetColProp((j * 14) + (2 + i*2), "Width", 0);
			grdHitRace.SetColProp((j * 7) + (2 + i), "Width", 0);
		}
    }
}

function dsOutVoilDesc_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
		var sVoilContent  = "";
		var sVoilContent2 = "";
		taVoilAct.Text    = "";

	    if( dsOutVoilDesc.GetRowCount() > 0 ) {
			for ( i = 0; i < dsOutVoilDesc.GetRowCount(); i++ ) { 
				if ( i == 0) sVoilContent += dsOutVoilDesc.GetColumn(i, "VOIL_DESC");
				else {
					if ( i < 15 )	sVoilContent  += "\n" + dsOutVoilDesc.GetColumn(i, "VOIL_DESC");	//√‚∑¬π∞ 15±Ó¡ˆ VOIL_DESC1ø°
					else			sVoilContent2 += "\n" + dsOutVoilDesc.GetColumn(i, "VOIL_DESC");	//√‚∑¬π∞ 16∫Œ≈Õ VOIL_DESC2ø°
				}
			}
			dsOutVoilDescPrint.AddRow();
			dsOutVoilDescPrint.SetColumn(0, "VOIL_DESC1", sVoilContent );
			dsOutVoilDescPrint.SetColumn(0, "VOIL_DESC2", sVoilContent2);

			taVoilAct.Text = sVoilContent + sVoilContent2;
		}	
}
