/*-------------------------------------------------------------------------------------------------+
>>  ÃÖÃÊ È­¸é LoadãÁ Ã³¸® ÇÒ »çÇ×
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
	//È­¸éÈ¯°æ ¼³Á¤
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//È­¸é Á¤º¸ Display ¹× Button ±ÇÇÑ ¼³Á¤
	sBUTTONLIST   = 'T' + sInpt + 'FTFT';

	if ( sInpt == "T" ) {
        btnRacerRecdInit.Visible = true;
        btnSearchRecd.Visible    = true;
        btnReturn.Visible        = true;
        btnSave.Visible          = true;
	} else {
        btnRacerRecdInit.Visible = false;
        btnSearchRecd.Visible    = false;
        btnReturn.Visible        = false;
        btnSave.Visible          = false;
	}


	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//ÃÊ±â È­¸é BK Color¼³Á¤ 
	fnc_SetBKColor(this);

    //³âµµÁ¶È¸
    fce_SetStndYear(dsOutSStndYear, toInteger(fce_GetSysdate("YYYY")));
    cboSStndYear.Index = 0;

    // ÇöÀç È¸Â÷ Á¶È¸
    fce_GetCurTms();

	//°øÅëÄÚµå Á¶È¸
	fce_GetComboDs(dsInComCd);

	// È­¸é ÃÊ±âÈ­
	fcInit();

	// Á¶È¸
	//fcSearch();
	fcSetParam();
}




