/*-------------------------------------------------------------------------------------------------+
>>  ´ÜÃàÅ° Ã³¸®(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key PressãÁ Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	if ( bControl && (nChar == 65 || nChar == 97) ) {
        var sServiceName = "e02050030-service:saveRecd";
        var sInDataSet   = " ";
        var sOutDataSet  = "dsOutRacerRecd=dsOutRacerRecd ";

        fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
        fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
        fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );

        fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	}

	//Control Key°¡ ´­·ÁÁ® ÀÖ´Ù¸é ´ÜÃàÅ° Ã³¸®
	//if (bControl) fnc_HotKey(this, nChar);
}

