function fcInit() {
    dsOutRacerRecd.ClearData();
    dsOutRacerRecdFly.ClearData();
    dsOutRacerRecdOnl.ClearData();
    dsOutMotRecd.ClearData();
    dsOutMotRecdFly.ClearData();
    dsOutMotRecdOnl.ClearData();
    dsOutBoatRecd.ClearData();
    dsOutBoatRecdFly.ClearData();
    dsOutBoatRecdOnl.ClearData();

    dsOutRacerRecdDetail.ClearData();
    dsOutMotRecdDetail.ClearData();
    dsOutBoatRecdDetail.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit();
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
    fce_AddParam(Trim("ALL_YN   "), chkAllYn.Value    );

    if ( chkAllYn.Value == "1" ) {
        sServiceName = "e02050030-service:searchRacerRecdAll";
    } else {
        sServiceName = "e02050030-service:searchRacerRecd";
    }
    sOutDataSet = "dsOutRacerRecd=dsOutRacerRecd "
                + "dsOutRacerRecdFly=dsOutRacerRecdFly "
                + "dsOutRacerRecdOnl=dsOutRacerRecdOnl "
                + "dsOutMotRecd=dsOutMotRecd "
                + "dsOutMotRecdFly=dsOutMotRecdFly "
                + "dsOutMotRecdOnl=dsOutMotRecdOnl "
                + "dsOutBoatRecd=dsOutBoatRecd "
                + "dsOutBoatRecdFly=dsOutBoatRecdFly "
                + "dsOutBoatRecdOnl=dsOutBoatRecdOnl ";

    fce_AddParam(Trim("RACER_NM "), tbMain.tbRacer.edRacerNm.Text );
    fce_AddParam(Trim("MOT_NO   "), tbMain.tbMot.edMotNo.Text     );
    fce_AddParam(Trim("BOAT_NO  "), tbMain.tbBoat.edBoatNo.Text   );
    fce_AddParam(Trim("ST_MTHD_ALL  "), "000"   ); //ÀüÃ¼Á¶È¸
    fce_AddParam(Trim("ST_MTHD_ONL  "), "001"   ); //¿Â¶óÀÎ½ºÅ¸Æ® Á¶È¸
    fce_AddParam(Trim("ST_MTHD_FLY  "), "002"   ); //ÇÃ¶óÀ×½ºÅ¸Æ® Á¶È¸

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  °¢ ¼±¼ö,Àåºñº° ¼ºÀû»ó¼¼Á¶È¸
+-------------------------------------------------------------------------------------------------*/
function fcSearchRecdCheck() {

    tbMain.tbRacer.chkAll.Value = "1";
	return true;
}

function fcSearchRecd(sType, sStMthdCd) {
    if ( !fcSearchRecdCheck() ) return;

	var sServiceName = "e02050030-service:searchRecdDetail";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTemp=dsOutRecdDetail ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );

	if ( sType == "RACER" ) {
        sOutDataSet  = "dsOutRacerRecdDetail=dsOutRecdDetail ";
        if ( fnc_IsNull(dsOutRacerRecd.GetColumn(dsOutRacerRecd.currow, "RACER_NO")) ) return;
        fce_AddParam(Trim("RACER_NO "), dsOutRacerRecd.GetColumn(dsOutRacerRecd.currow, "RACER_NO") );
        if(sStMthdCd = "001") fce_AddParam(Trim("RACER_NO "), dsOutRacerRecdOnl.GetColumn(dsOutRacerRecdOnl.currow, "RACER_NO") );
        if(sStMthdCd = "002") fce_AddParam(Trim("RACER_NO "), dsOutRacerRecdFly.GetColumn(dsOutRacerRecdFly.currow, "RACER_NO") );
	}
	if ( sType == "MOT" ) {
        sOutDataSet  = "dsOutMotRecdDetail=dsOutRecdDetail ";
        if ( fnc_IsNull(dsOutMotRecd.GetColumn(dsOutMotRecd.currow, "MOT_NO")) ) return;
        fce_AddParam(Trim("MOT_NO   "), dsOutMotRecd.GetColumn(dsOutMotRecd.currow, "MOT_NO") );
        if(sStMthdCd = "001") fce_AddParam(Trim("MOT_NO "), dsOutMotRecdOnl.GetColumn(dsOutMotRecdOnl.currow, "MOT_NO") );
        if(sStMthdCd = "002") fce_AddParam(Trim("MOT_NO "), dsOutMotRecdFly.GetColumn(dsOutMotRecdFly.currow, "MOT_NO") );
	}
	if ( sType == "BOAT" ) {
        sOutDataSet  = "dsOutBoatRecdDetail=dsOutRecdDetail ";
        if ( fnc_IsNull(dsOutBoatRecd.GetColumn(dsOutBoatRecd.currow, "BOAT_NO")) ) return;
        fce_AddParam(Trim("BOAT_NO  "), dsOutBoatRecd.GetColumn(dsOutBoatRecd.currow, "BOAT_NO") );
        if(sStMthdCd = "001") fce_AddParam(Trim("BOAT_NO  "), dsOutBoatRecdOnl.GetColumn(dsOutBoatRecdOnl.currow, "BOAT_NO") );
        if(sStMthdCd = "002") fce_AddParam(Trim("BOAT_NO  "), dsOutBoatRecdFly.GetColumn(dsOutBoatRecdFly.currow, "BOAT_NO") );

	}
	fce_AddParam(Trim("ST_MTHD_CD"), sStMthdCd);
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  ÀúÀå ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

    if ( dsOutRacerRecd.GetRowCount() == 0 ) {
		//ÀúÀå ÇÒ ÀÚ·á°¡ ¾øÀ½
		fnc_Message(SNIS_COM_1006, "N");
        return false;
    }

    fnc_CopyDataset(dsInRacer, dsOutRacerRecd, "chk", 1);

    if ( dsInRacer.GetRowCount() == 0 ) {
		//ÀúÀå ÇÒ ÀÚ·á°¡ ¾øÀ½
		fnc_Message(SNIS_COM_1006, "N");
        return false;
    }

    return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e02050030-service:saveRecd";
    var sInDataSet   = "dsOutRacerRecd=dsOutRacerRecd "
                     + "dsOutRacerRecdFly=dsOutRacerRecdFly "
                     + "dsOutRacerRecdOnl=dsOutRacerRecdOnl "
                     + "dsOutMotRecd=dsOutMotRecd "
                     + "dsOutMotRecdFly=dsOutMotRecdFly "
                     + "dsOutMotRecdOnl=dsOutMotRecdOnl "
                     + "dsOutBoatRecd=dsOutBoatRecd "
                     + "dsOutBoatRecdFly=dsOutBoatRecdFly "
                     + "dsOutBoatRecdOnl=dsOutBoatRecdOnl ";
    var sOutDataSet  = "dsOutRacerRecd=dsOutRacerRecd "
                     + "dsOutRacerRecdFly=dsOutRacerRecdFly "
                     + "dsOutRacerRecdOnl=dsOutRacerRecdOnl "
                     + "dsOutMotRecd=dsOutMotRecd "
                     + "dsOutMotRecdFly=dsOutMotRecdFly "
                     + "dsOutMotRecdOnl=dsOutMotRecdOnl "
                     + "dsOutBoatRecd=dsOutBoatRecd "
                     + "dsOutBoatRecdFly=dsOutBoatRecdFly "
                     + "dsOutBoatRecdOnl=dsOutBoatRecdOnl ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("APLY_DT  "), calEndDt.Value    );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

