/*-------------------------------------------------------------------------------------------------+
>>  »ó¼¼¼ºÀû Double Click½Ã °æÁÖ»ó¼¼Á¤º¸ Á¶È¸
+-------------------------------------------------------------------------------------------------*/
function tbMain_tbRacer_grdRacerRecdDetail_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    objDs = object(obj.BindDataset);
	if ( objDs.GetRowCount() == 0 ) return;
	if ( fnc_IsNull(objDs.GetColumn(objDs.currow, "STND_YEAR")) 
	  || fnc_IsNull(objDs.GetColumn(objDs.currow, "MBR_CD"   )) 
	  || fnc_IsNull(objDs.GetColumn(objDs.currow, "TMS"      )) 
	  || fnc_IsNull(objDs.GetColumn(objDs.currow, "DAY_ORD"  )) 
	  || fnc_IsNull(objDs.GetColumn(objDs.currow, "RACE_NO"  )) 
	   ) {
        return;
	}

	var sArg = " Arg_sStndYear=" + quote(objDs.GetColumn(objDs.currow, "STND_YEAR"))
	         + " Arg_sMbrCd="    + quote(objDs.GetColumn(objDs.currow, "MBR_CD"   ))
	         + " Arg_sTms="      + quote(objDs.GetColumn(objDs.currow, "TMS"      ))
	         + " Arg_sDayOrd="   + quote(objDs.GetColumn(objDs.currow, "DAY_ORD"  ))
	         + " Arg_sRaceNo="   + quote(objDs.GetColumn(objDs.currow, "RACE_NO"  ));

    var bReturn = Dialog("form::E02040044.xml", sArg, 700, 360, "");
}

