function fcSearchCheck(obj) {
	var sDt  = cboSStndYear.Value;
	var sTms = cboSRaceTms.Value;
	var eTms = cboERaceTms.Value;

	if(    fnc_isNull(sDt) ) {
        fnc_message(L_COM_ALT_0024, "N", "시작년도가 ");
        cboSStndYear.Value = "";
        return false;
	}	
/*	
	if(    !fnc_isNull(sTms) 
        && !fnc_isNull(eTms)
        && toInteger(sTms) > toInteger(eTms) ) {
        fnc_message(L_COM_ALT_0024, "N", "시작회차가 종료회차보다 이후일 수");
        return false;
	}		
*/
    fcInit(obj);
 	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerRankAcdnt=dsOutRacerRankAcdnt ";

	if (chkAllRecord.Value == "1") sServiceName = "e02060030-service:searchRacerRankAcdnt3";
	else if (edRacerNo.Text == "") sServiceName = "e02060030-service:searchRacerRankAcdnt";
	else                           sServiceName = "e02060030-service:searchRacerRankAcdnt2";

	fce_AddParam("SSTND_YEAR", cboSStndYear.Value);
	fce_AddParam("MBR_CD"    , cboSMbr.Value     );
	fce_AddParam("STMS"      , cboSRaceTms.Value );
	fce_AddParam("ETMS"      , cboERaceTms.Value );
	fce_AddParam("RACER_NO"  , edRacerNo.Text    );
	fce_AddParam(Trim("ST_MTHD_CD"),cboStMthdCd.value 	); //온라인스타트여부

    var nTmsRow        = fnc_GetExistValue(dsOutERaceTms, "TMS", cboERaceTms.Value);           // 해당 회차 주선 완료 여부
    var sTmsStrDt      = dsOutERaceTms.GetColumn(nTmsRow, "STR_DT");
    var sTmsEndDt      = dsOutERaceTms.GetColumn(nTmsRow, "END_DT");

	fce_AddParam("STR_DT"    , calStrDt.Value);
	fce_AddParam("END_DT"    , calEndDt.Value);
	fce_AddParam("TMS_STR_DT", sTmsStrDt);
	fce_AddParam("TMS_END_DT", sTmsEndDt);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


