function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e02070030-service:searchTrngAsk";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngAsk=dsOutTrngAsk "
                     + "dsOutTrngAskRacer=dsOutTrngAskRacer ";

	fce_AddParam(Trim("TRNG_ASK_SEQ"), edTrngAskSeq.Text);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutTrngAsk) && !fnc_DatasetChangeCheck(dsOutTrngAskRacer) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

    if ( !fnc_IsMandatory(cboTrngPlc, "훈련장") ) return false;
    if ( !fnc_IsMandatory(calStrDt  , "시작일") ) return false;
    if ( !fnc_IsMandatory(calEndDt  , "종료일") ) return false;

    if ( calEndDt.Value < calStrDt.Value ) {
        fnc_Message(SNIS_BOA_B011, "N", "시작일", "종료일");
        return false;
    }

    // if ( fce_GetSysdate("YYYYMMDD") > calStrDt.Value ) {
        // fnc_Message(SNIS_BOA_B013, "N", "시작일", "현재일");
        // return false;
    // }

    if ( dsOutTrngAskRacer.GetRowCount() == 0 ) {
        fnc_Message(SNIS_BOA_B014, "N", "선수");
        return false;
    }

 	// 필수항목체크
	oDataSet          = dsOutTrngAskRacer;
	sMandatoryColID   = "RACER_NO";
	sMandatoryColName = "등록번호";
	oGrid             = grdTrngAskRacer;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	// 멘토 그룹내 멘토 1명 이상 존재 여부 체크
	dsOutTrngAskRacer.fireEvent = false;
	dsOutTrngAskRacer.filter("MENTO_GRP_NO > 0");
	dsOutTrngAskRacer.Sort("MENTO_GRP_NO");
	var nGrpNo = 0;		//멘토그룹번호
	var NGrpNoCnt = 0;	//멘토인원수
	for (var icnt=0;icnt<dsOutTrngAskRacer.RowCount();icnt++) {
		if (nGrpNo <> ToNumber(dsOutTrngAskRacer.GetColumn(icnt, "MENTO_GRP_NO"))) {
			nGrpNo = ToNumber(dsOutTrngAskRacer.GetColumn(icnt, "MENTO_GRP_NO"));
			NGrpNoCnt = 0 ;
		}
		if (dsOutTrngAskRacer.GetColumn(icnt, "MENTO_YN") == "1") {
			if (NGrpNoCnt == 0) NGrpNoCnt ++;
			else {
				fnc_message("멘토그룹내 2명의 멘토를 선택할 수 없습니다.");
				return false;
			}
		}
	}
	dsOutTrngAskRacer.UnFilter();
	dsOutTrngAskRacer.fireEvent = true;

	return true;
}

function fcSave(obj) {
	// 훈련중인 경우 저장할 수 없다.(2012.12.13)
	var sTrngAskStatCd = dsOutTrngAsk.GetColumn(0, "TRNG_ASK_STAT_CD");
	if (sTrngAskStatCd <> "000") {
		if (sTrngAskStatCd == "001") {
			fnc_message("수시훈련조회화면에서 훈련요청을 취소후 저장하세요!");
		} else if (sTrngAskStatCd == "002") {
			fnc_message("훈련원에 해당 차수의 접수취소를 요청하세요.");
		}
		return;
	}

    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e02070030-service:saveTrngAsk";
    var sInDataSet   = "dsOutTrngAsk=dsOutTrngAsk "
                     + "dsOutTrngAskRacer=dsOutTrngAskRacer:u ";
    var sOutDataSet  = " ";

	fce_AddParam(Trim("TRNG_ASK_SEQ   "), edTrngAskSeq.Text);
	fce_AddParam(Trim("TRNG_PLC_CD    "), cboTrngPlc.Value );
	fce_AddParam(Trim("TRNG_ASK_STR_DT"), calStrDt.Value   );
	fce_AddParam(Trim("TRNG_ASK_END_DT"), calEndDt.Value   );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

