/*-------------------------------------------------------------------------------------------------+
>>  훈련가능한선수 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchRacerListCheck() {
    if ( !fnc_IsMandatory(calStrDt  , "훈련요청 시작일") ) return false;
    if ( !fnc_IsMandatory(calEndDt  , "훈련요청 종료일") ) return false;
    //if ( !fnc_IsMandatory(calSStrDt , "예정선수등록 시작일") ) return false;
    //if ( !fnc_IsMandatory(calSEndDt , "예정선수등록 종료일") ) return false;

    if ( calEndDt.Value < calStrDt.Value ) {
        fnc_Message(SNIS_BOA_B011, "N", "훈련요청 시작일", "훈련요청 종료일");
        return false;
    }

    /*if ( calSEndDt.Value < calSStrDt.Value ) {
        fnc_Message(SNIS_BOA_B013, "N", "예정선수등록 시작일", "예정선수등록 종료일");
        return false;
    }*/

    return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  훈련가능한선수 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchRacerList(obj) {
    if ( !fcSearchRacerListCheck(obj) ) return;

	var sServiceName = "e02070030-service:searchTrngAskRacerList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngAskRacerList=dsOutTrngAskRacerList ";

	fce_AddParam(Trim("STR_DT     "), calStrDt.Value );
	fce_AddParam(Trim("END_DT     "), calEndDt.Value );
	//fce_AddParam(Trim("RECD_STR_DT"), calSStrDt.Value);
	//fce_AddParam(Trim("RECD_END_DT"), calSEndDt.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

