/*-------------------------------------------------------------------------------------------------+
>>  훈련요청선수추가
+-------------------------------------------------------------------------------------------------*/
function btnAddRacer_OnClick(obj)
{
	/*
	if ( fnc_GetExistValue(dsOutTrngAskRacerList, "chk", "1") < 0 ) {
        fnc_Message(SNIS_BOA_B014, "N", "선수");
        return;
	}
	*/

	var nRacerRow = dsOutTrngAskRacer.GetRowCount();
	for ( var i = 0; i < dsOutTrngAskRacerList.GetRowCount(); i++ ) {
        if ( dsOutTrngAskRacerList.GetColumn(i, "chk") == "1" ) {
            if ( fnc_GetExistValue(dsOutTrngAskRacer, "RACER_NO", dsOutTrngAskRacerList.GetColumn(i, "RACER_NO")) < 0 ) {
                /* 인원수 제한없음(2012.10.20)
                if ( nRacerRow >= 24 ) {
                    fnc_Message("24명이상 선택 하실 수 없습니다.", "N");
                    break;
                }
                */
                dsOutTrngAskRacer.AddRow();
                dsOutTrngAskRacer.CopyRow(nRacerRow++, dsOutTrngAskRacerList, i);
            }
        }
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  훈련요청선수추가(Grid Double Click시)
+-------------------------------------------------------------------------------------------------*/
function grdTrngAskRacerList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var nRacerRow = dsOutTrngAskRacer.GetRowCount();
    if ( fnc_GetExistValue(dsOutTrngAskRacer, "RACER_NO", dsOutTrngAskRacerList.GetColumn(nRow, "RACER_NO")) < 0 ) {
        /* 인원수 제한없음(2012.10.20)
        if ( nRacerRow >= 24 ) {
            fnc_Message("24명이상 선택 하실 수 없습니다.", "N");
            break;
        }
        */
        dsOutTrngAskRacer.AddRow();
        dsOutTrngAskRacer.CopyRow(nRacerRow++, dsOutTrngAskRacerList, i);
    }
}

function fcOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (obj.id == "dsOutTrngAskRacer") {
		if (strColumnID == "MENTO_GRP_NO") {
			if (varNewValue == 0) {
				fnc_message("멘토 그룹은 0보다 큰 값으로 입력해 주세요!!");
				dsOutTrngAskRacer.SetColumn(nRow, strColumnID, varOldValue);
				dsOutTrngAskRacer.row = nRow;
			}
		} else if (strColumnID == "MENTO_YN") {
			if (varNewValue == 1) {
				var maxGrpNo = 0;
				var curGrpNo = 0;
				for (var iRow=0;iRow<dsOutTrngAskRacer.rowcount();iRow++) {
					if (dsOutTrngAskRacer.GetColumn(iRow, "MENTO_YN") == 1) {
						curGrpNo = toNumber(dsOutTrngAskRacer.GetColumn(iRow,"MENTO_GRP_NO"));
						if (maxGrpNo<curGrpNo) {
							maxGrpNo = curGrpNo;
						}
					}
				}
				maxGrpNo ++;
				dsOutTrngAskRacer.SetColumn(nRow, "MENTO_GRP_NO", maxGrpNo);
			} else {
				dsOutTrngAskRacer.SetColumn(nRow, "MENTO_GRP_NO", null);
			}
		}
	}
}
