/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {
	if(length(edTrngAskSeq.Value) <= 0){	
		//의뢰번호는 필수 조건
		fnc_Message(SNIS_CAN_A012, "N");
		return false;
	}

	fcInit(obj);
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "e02070041-service:searchRacerTrngRslt";
    var sInDataSet   = "";
    var sOutDataSet  = " dsRacerTrngRslt=dsRacerTrngRslt ";
	    sOutDataSet += " dsRacerTrngRsltStart=dsRacerTrngRsltStart ";
	    sOutDataSet += " dsRacerTrngRsltMngTech=dsRacerTrngRsltMngTech ";
	    sOutDataSet += " dsRacerTrngRsltManiEx=dsRacerTrngRsltManiEx ";

	fce_AddParam("TRNG_ASK_SEQ", edTrngAskSeq.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}
