/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	var nCol = nY;	
	var nColNm = "";	
	var sVal = "001";
	if (obj.GetCellText("Head", -1, nCell) == "판정" || 
	    obj.GetCellText("Head", -1, nCell) == "이수여부") {	
		var objDS;		
		if (tab0.TabIndex == 0) {
			nColNm = "CMPL_YN";
			objDS = object("dsRacerTrngRslt");		
			if (objDS.GetColumn(0, nColNm) == "Y") sVal = "N";
			else sVal = "Y";
		} else {
			nColNm = "RSLT";
			switch (tab0.TabIndex) {
				case 1: 
					objDS = object("dsRacerTrngRsltStart");
					break;
				case 2:
					objDS = object("dsRacerTrngRsltMngTech");
					break;
				case 3:
					objDS = object("dsRacerTrngRsltManiEx");
					break;
				default :
					return;
			}
			if (objDS.GetColumn(0, nColNm) == "001") sVal = "002";
		}
		for (var iRow=0;iRow<objDS.RowCount();iRow++) {
			objDS.SetColumn(iRow, nColNm, sVal);
		}
	} else {
		fnc_GridSort(obj, object(obj.BindDataset), nCell);
	}
}

