/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

   if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 저장후 조회
	if ( sServiceName == "e02070041-service:searchRacerTrngRslt") {
		if (dsRacerTrngRsltStart.rowCount() > 0) {
			tab0.tab2.calEstiDt.Value = dsRacerTrngRsltStart.GetColumn(0,"ESTI_DT");
			tab0.tab2.stUserName.text = dsRacerTrngRsltStart.GetColumn(0,"ESTIER_NM");
		}
		if (dsRacerTrngRsltMngTech.rowCount() > 0) {
			tab0.tab3.calEstiDt.Value = dsRacerTrngRsltMngTech.GetColumn(0,"ESTI_DT");
			tab0.tab3.stUserName.text = dsRacerTrngRsltMngTech.GetColumn(0,"ESTIER_NM");
		}
		if (dsRacerTrngRsltManiEx.rowCount() > 0) {
			tab0.tab4.calEstiDt.Value = dsRacerTrngRsltManiEx.GetColumn(0,"ESTI_DT");
			tab0.tab4.stUserName.text = dsRacerTrngRsltManiEx.GetColumn(0,"ESTIER_NM");
		}

	}

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

// 초기값 설정
function fcSetDefault() {
	// 의뢰번호, 훈련시작일자, 훈련종료일자 
	var sAskSeq = Arg_TrngAskSeq;

    edTrngAskSeq.Text = trim(sAskSeq);

    fcSearch();
}






function fcSelUser(obj)
{
	var args  = "";
	var bRtn = Dialog("common::comEmpSearch.xml", args, 568, 512, "");
	if(bRtn) {
		switch(tab0.TabIndex) {
			case 1:
				for(var iRow=0;iRow<dsRacerTrngRsltStart.GetRowCount();iRow++) {
					dsRacerTrngRsltStart.SetColumn(iRow, "ESTIER", G_ds_return.GetColumn(0, "USER_id"));
				}
				tab0.tab2.stUserId.text 	= G_ds_return.GetColumn(0, "USER_ID");
				tab0.tab2.stUserName.text 	= G_ds_return.GetColumn(0, "USER_NM");				
				break;

			case 2:
				for(var iRow=0;iRow<dsRacerTrngRsltMngTech.GetRowCount();iRow++) {
					dsRacerTrngRsltMngTech.SetColumn(iRow, "ESTIER", G_ds_return.GetColumn(0, "USER_id"));
				}
				tab0.tab3.stUserId.text 	= G_ds_return.GetColumn(0, "USER_ID");
				tab0.tab3.stUserName.text 	= G_ds_return.GetColumn(0, "USER_NM");				
				break;

			case 3:
				for(var iRow=0;iRow<dsRacerTrngRsltManiEx.GetRowCount();iRow++) {
					dsRacerTrngRsltManiEx.SetColumn(iRow, "ESTIER", G_ds_return.GetColumn(0, "USER_id"));
				}
				tab0.tab4.stUserId.text 	= G_ds_return.GetColumn(0, "USER_ID");
				tab0.tab4.stUserName.text 	= G_ds_return.GetColumn(0, "USER_NM");				
				break;
		}		
	}
}

function Tab0_tab3_grdRacerTrngRslt_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	var maxScore = 0;
	switch(nOldCell)
	{
		case 2:
			maxScore = 10;
			break;
		case 3:
		case 4:
			maxScore = 5;
			break;
		case 6:
		case 7:
		case 9:
		case 10:
			maxScore = 20;
			break;
		default :
			return;
	}
	if (dsRacerTrngRsltManiEx.GetColumn(nOldRow, nOldCell) > MaxScore) {
		fnc_message("배점한도는 "+maxScore+"점입니다. \n평가점수를 수정해 주시기 바랍니다.","N");
		return false;
	}

}


function Tab0_OnChanged(obj,nOldIndex,nNewindex)
{
	if (nNewIndex <> 0) stInfo.visible = true;
	else                stInfo.visible = false;
}

function fcOnChanged(obj,strOldText,strNewText)
{
	var objDS;
	var valCal = "";
	switch (tab0.TabIndex) {
		case 1: 
			objDS  = object("dsRacerTrngRsltStart");
			valCal = tab0.tab2.calEstiDt.Value;
			break;
		case 2:
			objDS = object("dsRacerTrngRsltMngTech");
			valCal = tab0.tab3.calEstiDt.Value;
			break;
		case 3:
			objDS = object("dsRacerTrngRsltManiEx");
			valCal = tab0.tab4.calEstiDt.Value;
			break;
		default :
			return;
	}
	for (var iRow=0;iRow<objDS.RowCount();iRow++) {
		objDS.SetColumn(iRow, "ESTI_DT", strNewText);
	}
}
