/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	if(fnc_isNull(cboSStndYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboSStndYear.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSMbr.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경정장");
            cboSMbr.SetFocus();
            return false;
    } 
    if(fnc_isNull(cboSRaceTms.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            cboSRaceTms.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSDayOrd.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "일차");
            cboSDayOrd.SetFocus();
            return false;
    } 
	if(fnc_isNull(cboSRaceNo.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경주번호");
            cboSRaceNo.SetFocus();
            return false;
    }

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    //경주결과조회 
	var sServiceName = "e04020010-service:searchRace";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRace=dsOutRace";
    dsOutRace.ClearData();

    fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), cboSDayOrd.Value  );
    fce_AddParam(Trim("RACE_NO  "), cboSRaceNo.Value  );
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);


    //경주결과조회(편성)
    var sServiceName = "e04020010-service:searchOrgan";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrgan=dsOutOrgan";
    dsOutOrgan.ClearData();

    fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), cboSDayOrd.Value  );
    fce_AddParam(Trim("RACE_NO  "), cboSRaceNo.Value  );
    fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), cboSDayOrd.Value  );
    fce_AddParam(Trim("RACE_NO  "), cboSRaceNo.Value  );
    fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), cboSDayOrd.Value  );

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutPrizeRace) 
	  && !fnc_DatasetChangeCheck(dsOutEventRace) 
	  && !fnc_DatasetChangeCheck(dsOutMapRaceInfo)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutPrizeRace;
	sMandatoryColID   = "CD_NM";
	sMandatoryColName = "대상경주그룹명";
	oGrid             = tabGrp.tabPrizePage.grdPrize;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	// 필수항목체크
	oDataSet          = dsOutEventRace;
	sMandatoryColID   = "CD_NM";
	sMandatoryColName = "이벤트경주그룹명";
	oGrid             = tabGrp.tabEventPage.grdEvent ;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}	

	// 필수항목체크
	oDataSet          = dsOutMapRaceInfo;
	sMandatoryColID   = "PRIZ_RACE_NM";
	sMandatoryColName = "대상경주명";
	oGrid             = grdMapping;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

	// dsOutPrizeRace dsOutEventRace dsOutMapRaceInfo

    if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName = "e02080200-service:saveMapping";
    sInDataSet   = "dsOutPrizeRace=dsOutPrizeRace:u ";
    sInDataSet  += "dsOutEventRace=dsOutEventRace:u ";
    sInDataSet  += "dsOutMapRaceInfo=dsOutMapRaceInfo:u ";

    sOutDataSet  = "";

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

