//년도변경시
function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");


}
//경정장변경시
function cboSMbr_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cboSStndYear.Value == "" || cboSStndYear.Value == null) 	return ;
	if(cboSMbr.Value 	  == "" || cboSMbr.Value == null) 		return ;
	if(cboSRaceTms.Value  == "" || cboSRaceTms.Value == null) 	return ;
	if(cboSDayOrd.Value   == "" || cboSDayOrd.Value == null) 	return ;
	if(cboSRaceNo.Value   == "" || cboSRaceNo.Value  == null) 	return ;
	fcSearch();	
}

//회차변경시
function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSDayOrd");

	if(cboSStndYear.Value == "" || cboSStndYear.Value == null) 	return ;
	if(cboSMbr.Value 	  == "" || cboSMbr.Value == null) 		return ;
	if(cboSRaceTms.Value  == "" || cboSRaceTms.Value == null) 	return ;
	if(cboSDayOrd.Value   == "" || cboSDayOrd.Value == null) 	return ;
	if(cboSRaceNo.Value   == "" || cboSRaceNo.Value  == null) 	return ;
	fcSearch();
}
//일차변경시
function cboSDayOrd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//경주일자셋팅
	dsOutSDayOrd.row = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", cboSDayOrd.Value);
	fce_GetRace(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, cboSDayOrd.Value, "dsOutSRace");
	if(cboSStndYear.Value == "" || cboSStndYear.Value == null) 	return ;
	if(cboSMbr.Value 	  == "" || cboSMbr.Value == null) 		return ;
	if(cboSRaceTms.Value  == "" || cboSRaceTms.Value == null) 	return ;
	if(cboSDayOrd.Value   == "" || cboSDayOrd.Value == null) 	return ;
	if(cboSRaceNo.Value   == "" || cboSRaceNo.Value  == null) 	return ;
	fcSearch();
}
//경주번호변경시
function cboSRaceNo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{   
    if(cboSStndYear.Value == "" || cboSStndYear.Value == null) 	return ;
	if(cboSMbr.Value 	  == "" || cboSMbr.Value == null) 		return ;
	if(cboSRaceTms.Value  == "" || cboSRaceTms.Value == null) 	return ;
	if(cboSDayOrd.Value   == "" || cboSDayOrd.Value == null) 	return ;
	if(cboSRaceNo.Value   == "" || cboSRaceNo.Value  == null) 	return ;
    fcSearch(); 
}

//대상(이벤트)경주그룹명 조회
function fcSearchGrpCd()
{	
	//대상경주그룹 조회
	var sServiceName = "e02080200-service:searchPrizGrp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutPrizeRace=dsOutPrizeRace";

    fce_AddParam(Trim("GRP_CD"), "E00139");

    http.sync= true;
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
    http.sync= false;

	//이벤트경주그룹 조회
	var sServiceName = "e02080200-service:searchPrizGrp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutEventRace=dsOutPrizeRace";

    fce_AddParam(Trim("GRP_CD"), "E00142");

    http.sync= true;
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
    http.sync= false;    


}

// 맵핑정보조회
function fcSearchMapRaceInfo(grpCd, cd)
{
	var sServiceName = "e02080200-service:searchMapRaceInfo";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutMapRaceInfo=dsOutMapRaceInfo";

    fce_AddParam(Trim("GRP_CD"), grpCd);
    fce_AddParam(Trim("CD"), cd);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	var addIdx = -1;
	if(tabGrp.TabIndex==0 ){
		addIdx = dsOutPrizeRace.AddRow();
		dsOutPrizeRace.SetColumn(addIdx, "GRP_CD", "E00139");
		if(dsOutPrizeRace.GetRowCount()==0){
			dsOutPrizeRace.SetColumn(addIdx, "CD", "001"); 
		}else{
			dsOutPrizeRace.SetColumn(addIdx, "CD", Lpad(toInteger(dsOutPrizeRace.Max("CD"))+1, "0", 3)); 
		}

	}else if(tabGrp.TabIndex==1 ){
		addIdx = dsOutEventRace.AddRow();
		dsOutEventRace.SetColumn(addIdx, "GRP_CD", "E00142");
		if(dsOutEventRace.GetRowCount()==0){
			dsOutEventRace.SetColumn(addIdx, "CD", "001"); 
		}else{
			dsOutEventRace.SetColumn(addIdx, "CD", Lpad(toInteger(dsOutEventRace.Max("CD"))+1, "0", 3)); 
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	if(obj.GetForm().id=="grpBtnRow"){

		if(tabGrp.TabIndex==0 ){
			if ( !fnc_DeleteCheck(dsOutPrizeRace, "CHK", "1") ) return;
			dsOutMapRaceInfo.ClearData();
		}else if(tabGrp.TabIndex==1 ){
			if ( !fnc_DeleteCheck(dsOutEventRace, "CHK", "1") ) return;
			dsOutMapRaceInfo.ClearData();
		}

	}else if(obj.GetForm().id!="grpBtnRow" && obj.id == "btnDelRow"){
		if ( !fnc_DeleteCheck(dsOutMapRaceInfo, "CHK", "1") ) return;	
	}
}

// 해당경주매핑
function btnDsOutRaceReport_OnClick(obj)
{
	// 조회된 경주가 없을 경우
	if(dsOutRace.GetRowCount()==0){
		fnc_Message("경주정보가 없습니다. 매핑대상 경주조회 후 매핑하시기 바랍니다.");
		return;
	}

	// 경주그룹이 선택되지 않았을경우
	if((tabGrp.TabIndex==0 && dsOutPrizeRace.GetRowPos()==-1)||(tabGrp.TabIndex==1 && dsOutEventRace.GetRowPos()==-1)) return;

	// 중복체크
	for ( i = 0; i < dsOutMapRaceInfo.GetRowCount(); i++ ) {         
		if(dsOutMapRaceInfo.GetColumn(i, "STND_YEAR") == dsOutRace.GetColumn(0, "STND_YEAR") &&
			dsOutMapRaceInfo.GetColumn(i, "MBR_CD") == dsOutRace.GetColumn(0, "MBR_CD") &&
			dsOutMapRaceInfo.GetColumn(i, "TMS") == dsOutRace.GetColumn(0, "TMS") &&
			dsOutMapRaceInfo.GetColumn(i, "DAY_ORD") == dsOutRace.GetColumn(0, "DAY_ORD") &&
			dsOutMapRaceInfo.GetColumn(i, "RACE_NO") == dsOutRace.GetColumn(0, "RACE_NO")
			){
			fnc_Message("해당경주는 이미 매핑되어 있습니다.");
			return;
		}
	} 

	// 맵핑정보 생성
	var mapIdx = dsOutMapRaceInfo.AddRow();

	if(tabGrp.TabIndex==0 ){
		dsOutMapRaceInfo.SetColumn(mapIdx, "GRP_CD", dsOutPrizeRace.GetColumn(dsOutPrizeRace.GetRowPos(), "GRP_CD") );
		dsOutMapRaceInfo.SetColumn(mapIdx, "CD", dsOutPrizeRace.GetColumn(dsOutPrizeRace.GetRowPos(), "CD") );
		dsOutMapRaceInfo.SetColumn(mapIdx, "CD_NM", dsOutPrizeRace.GetColumn(dsOutPrizeRace.GetRowPos(), "CD_NM") );
	}else if(tabGrp.TabIndex==1 ){
		dsOutMapRaceInfo.SetColumn(mapIdx, "GRP_CD", dsOutEventRace.GetColumn(dsOutEventRace.GetRowPos(), "GRP_CD") );
		dsOutMapRaceInfo.SetColumn(mapIdx, "CD", dsOutEventRace.GetColumn(dsOutEventRace.GetRowPos(), "CD") );
		dsOutMapRaceInfo.SetColumn(mapIdx, "CD_NM", dsOutEventRace.GetColumn(dsOutEventRace.GetRowPos(), "CD_NM") );
	}

	dsOutMapRaceInfo.SetColumn(mapIdx, "STND_YEAR", dsOutRace.GetColumn(0, "STND_YEAR") );
	dsOutMapRaceInfo.SetColumn(mapIdx, "MBR_CD", dsOutRace.GetColumn(0, "MBR_CD") );
	dsOutMapRaceInfo.SetColumn(mapIdx, "TMS", "" + dsOutRace.GetColumn(0, "TMS") );
	trace(dsOutRace.GetColumn(0, "TMS"));
	trace(dsOutRace.GetColumn(0, "DAY_ORD"));
	dsOutMapRaceInfo.SetColumn(mapIdx, "DAY_ORD", "" + dsOutRace.GetColumn(0, "DAY_ORD") );
	dsOutMapRaceInfo.SetColumn(mapIdx, "RACE_NO", dsOutRace.GetColumn(0, "RACE_NO") );

	trace(dsOutMapRaceInfo.GetColumn(mapIdx, "TMS"));
	trace(dsOutMapRaceInfo.GetColumn(mapIdx, "DAY_ORD"));

}

// 대상경주그룹 셀 이동시 매핑정보 조회
function dsOutPrizeRace_OnRowPosChanged(obj,nOldRow,nRow)
{	
	fcSearchMapRaceInfo(obj.GetColumn(nRow, "GRP_CD"), obj.GetColumn(nRow, "CD"));	
	fcDisplayGrpNm(obj,nRow);
}

// 이벤트경주그룹 셀 이동시 매핑정보 조회
function dsOutEventRace_OnRowPosChanged(obj,nOldRow,nRow)
{
	fcSearchMapRaceInfo(obj.GetColumn(nRow, "GRP_CD"), obj.GetColumn(nRow, "CD"));	
	fcDisplayGrpNm(obj,nRow);
}

function fcDisplayGrpNm(obj,nRow) 
{
	if (obj.GetColumn(nRow, "CD") == "%") {
		stOutPrizeRace.text = "";
		btnMapping.Enable = false;
	} else {
		stOutPrizeRace.text = "경주그룹 : " + obj.GetColumn(nRow, "CD_NM");
		btnMapping.Enable = true;
	}
}

// 탭이동시 맵핑정보 초기화
function tabGrp_OnChanged(obj,nOldIndex,nNewindex)
{
	dsOutMapRaceInfo.Clear();
}
