// 선수조회 이벤트
function btnSearchRacer_OnClick(obj,nX,nY)
{

	var sResponse = Dialog("form::E03010012.xml", "", 834, 450, "");
    var sPtoName  = sResponse[3];
    var sPhysicalFileName = split(sPtoName, "//");
    var url = GBL_IMAGELINK_URL + sPhysicalFileName[length(sPhysicalFileName) - 1];

    if ( !fnc_IsNull(sResponse) ) {
		edtRacerNo.Text = sResponse[0];
		edtRacerNm.Text = sResponse[1];

		imgPhotoNm.ImageID = "";

		if (fnc_IsNull(sPtoName)) return;

		imgPhotoNm.ImageID = url;
		imgPhotoNm.FillType = "STRETCH";
    }
}


// 조회버튼 이벤트
function fcSearch(obj) {
    var sServiceName = ""; 
    var sInDataSet   = "";
    var sOutDataSet  = "";

    if(!fcSearchCheck()) return;

	sServiceName = "e02080310-service:searchRaceRefHuman";
	sInDataSet   = "";
	sOutDataSet  = "dsRacerRefHuman=dsRacerRefHuman dsRacerRefRace=dsRacerRefRace dsRacerRefRela=dsRacerRefRela dsRacerRefDial=dsRacerRefDial dsRacerVoilAct=dsRacerVoilAct";

    fce_AddParam(Trim("RACER_NO"), edtRacerNo.Value);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


// 조회버튼 클릭전 체크
function fcSearchCheck(obj) {
	// 조회항목체크
	if(length(edtRacerNo.Text) <= 0 && length(edtRacerNm.Text) <= 0) {
		fnc_Message("선수를 조회하신 후 진행하시기 바랍니다.", "N");	
		return false;
	}

	return true;
}


// 저장 이벤트
function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

	var sServiceName = "e02080310-service:saveRacerReference";
    var sInDataSet   = "dsRacerRefHuman=dsRacerRefHuman:u dsRacerRefRace=dsRacerRefRace:u dsRacerRefRela=dsRacerRefRela:u dsRacerRefDial=dsRacerRefDial:u";
    var sOutDataSet  = "";

	fce_AddParam(Trim("RACER_NO"), edtRacerNo.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", "U");
}


// 저장버튼 클릭 전 체크
function fcSaveCheck(obj) {
	// 선수코드 필수항목 체크
	if(length(edtRacerNo.Text) <= 0 && length(edtRacerNm.Text) <= 0) {
		fnc_Message("선수를 조회하신 후 상세정보를 저장하시기 바랍니다.", "N");	
		return false;
	}

	// dsRacerRefRela의 필수항목 체크
	for(i = 0; i < dsRacerRefRela.GetRowCount(); i++) {
		if(length(dsRacerRefRela.GetColumn(i, "RELA_RACER_CD")) <= 0 || 
		   length(dsRacerRefRela.GetColumn(i, "RELA_RACER_NO")) <= 0) {
			fnc_Message("선수관계/관계선수는 반드시 입력하셔야 합니다.", "N");	
			return false;
		}
	}

	// dsRacerRefDial 필수항목 체크
	for(i = 0; i < dsRacerRefDial.GetRowCount(); i++) {
		if(length(dsRacerRefDial.GetColumn(i, "DIARY_DATE")) <= 0  || 
		   length(dsRacerRefDial.GetColumn(i, "DIARY_HOUR")) <= 0  ||
		   length(dsRacerRefDial.GetColumn(i, "DIARY_MINUTE")) <= 0) {
			fnc_Message("작성일/시간/분은 반드시 입력하셔야 합니다.", "N");	
			return false;
		}
	}

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsRacerRefHuman) &&
		 !fnc_DatasetChangeCheck(dsRacerRefRace)  &&
		 !fnc_DatasetChangeCheck(dsRacerRefRela)  &&
		 !fnc_DatasetChangeCheck(dsRacerRefDial)) {

		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	

		return false;
	}

	return true;
}


// CallBack함수
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
		dsOutRelnCopy.ClearData();

		for(i = 0, j=0; i < dsOutReln.GetRowCount(); i++){
			if(substr(dsOutReln.GetColumn(i, "CD"), 0, 2) == "04") {
				dsOutRelnCopy.AddRow();
				dsOutRelnCopy.CopyRow(j++, "dsOutReln", i);
			}
		}

		if ( IsExistVar("Arg_sRacerNo") ) {
			edtRacerNm = Arg_sRacerNm;
			edtRacerNo = Arg_sRacerNo;
			imgPhotoNm.ImageID = Arg_sImgPhotoNm;
			imgPhotoNm.FillType = "STRETCH";
            fcSearch();     
        }


		return;
	}

	// 저장 후
	if ( sServiceName == "e02080310-service:saveRacerReference" ) {
		fcSearch();
        return;
	}

	// 선수 세부정보(인사)정보조회 후
	if ( sServiceName == "e02080310-service:searchRaceRefHuman" ) {
        fnc_SetStatus(SNIS_BOA_0003, dsRacerRefHuman.GetRowCount());
//trace(dsRacerRefHuman.saveXML());
   		if(dsRacerRefHuman.RowCount() <= 0 && length(edtRacerNo.Text) > 0) {
			dsRacerRefHuman.AddRow();
		}

		if(dsRacerRefRace.RowCount() <= 0 && length(edtRacerNo.Text) > 0) {
			dsRacerRefRace.AddRow();
		}

		if(dsRacerVoilAct.GetRowCount() > 0) {
			tbDetail1.tab5.staFirstDay.Visible = true;
			tbDetail1.tab5.staCurrDay.Text = dsRacerVoilAct.GetColumn(0, "CURR_DAY");
		}	
         return;
	}
}


// 초기화버튼 이벤트
function btnTabInit_OnClick(obj)
{
	if(length(edtRacerNo.Text) <= 0 && length(edtRacerNm.Text) <= 0) {
		fnc_Message("초기화버튼은 선수조회 후 사용하실 수 있습니다.", "N");	
		return false;
	}

	if(obj.id == "btnTab1Init") {			//tab1 초기화버튼
		dsRacerRefHuman.ClearData();
		dsRacerRefHuman.AddRow();
	} else if (obj.id == "btnTab2Init") {	//tab2 초기화버튼
		dsRacerRefRace.ClearData();
		dsRacerRefRace.AddRow();

	}

}


//  행추가 버튼 클릭 時 처리
function fcAddRow(obj) {
	if(length(edtRacerNo.Text) <= 0 && length(edtRacerNm.Text) <= 0) {
		fnc_Message("선수를 조회하신 후 진행하시기 바랍니다.", "N");	
		return false;
	}

	if(obj.id == "tab3btnAddRow") {
		dsRacerRefRela.AddRow();         //Record Add
	} else if(obj.id == "tab4btnAddRow") {
		dsRacerRefDial.AddRow();         //Record Add
	}
}


//  행삭제 버튼 클릭 時 처리
function fcDelRow(obj) {
	if(length(edtRacerNo.Text) <= 0 && length(edtRacerNm.Text) <= 0) {
		fnc_Message("선수를 조회하신 후 진행하시기 바랍니다.", "N");	
		return false;
	}

	if(obj.id == "tab3btnDelRow") {
		if ( !fnc_DeleteCheck(dsRacerRefRela, "chk", "1") ) return;
		dsRacerRefRela.SetFocus();
	} else if(obj.id == "tab4btnDelRow") {
		if ( !fnc_DeleteCheck(dsRacerRefDial, "chk", "1") ) return;
		dsRacerRefDial.SetFocus();
	}
}


//선수관계의 선수명 조회버튼 클릭시 처리
function tbDetail1_tab3_grdRacerRela_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var sResponse = Dialog("form::E03010012.xml", "", 834, 450, "");

	if(sResponse[0] == edtRacerNo.Text) {
		fnc_Message("자기 자신을 입력할 수는 없습니다. ", "N");	
		return false;
	}


    if ( !fnc_IsNull(sResponse) ) {
		dsRacerRefRela.SetColumn(nRow, "RELA_RACER_NO", sResponse[0]);
		dsRacerRefRela.SetColumn(nRow, "RELA_RACER_NM", sResponse[1]);
		dsRacerRefRela.SetColumn(nRow, "RELA_RACER_PERIO_NO", sResponse[4]);
    }
}


//  엑셀 버튼 클릭 時 처리
function fcToExcel(obj) {
	if(tbDetail1.TabIndex == 0 || tbDetail1.TabIndex == 1) {
		fnc_Message("[선수관계|면담일지|출발위반] Tab에서만 엑셀버튼을 이용할 수 있습니다.", "N");
		return false;
	} else if(tbDetail1.TabIndex == 2) {
		fnc_OpenExcelExport("grdRacerRela");   
	} else if(tbDetail1.TabIndex == 3) {
		fnc_OpenExcelExport("grdRacerRela");   
	} else if(tbDetail1.TabIndex == 4) {
		fnc_OpenExcelExport("grdRacerVoilAct");   
	}

}


