	sRptNm    = "e02080310_racer_interview.jrf";               //레포트 파일명
    sDataSet  = "racerRefHuman=dsRacerRefHuman racerRefRace=dsRacerRefRace racerRefRela=dsRacerRefRela racerRefDial=dsRacerRefDial racerVoilAct=dsRacerVoilAct";   //"UBI 쿼리명, MIP Dataset명"

