function fcInit(obj) {	
    tbDetail1.TabIndex = 0;
    tbDetail2.TabIndex = 0;

    dsOutRacer.ClearData();
    dsOutRacerFam.ClearData();
    dsOutRacerAcad.ClearData();
    dsOutRacerRela.ClearData();
    dsOutRacerPrz.ClearData();
    dsOutRacerAward.ClearData();
    dsOutRacerIjry.ClearData();
    dsOutRacerSanc.ClearData();

    edPtoNm.Text = "";

    dsOutRacer.AddRow();
    /*dsOutRacerFam.AddRow();
    dsOutRacerAcad.AddRow();
    dsOutRacerRela.AddRow();*/
    rdoLunSol.Index = 1;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e03010010-service:searchRacerDetl";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacer=dsOutRacer  "
                     + "dsOutRacerFam=dsOutRacerFam  "
                     + "dsOutRacerAcad=dsOutRacerAcad  "
                     + "dsOutRacerRela=dsOutRacerRela  "
                     + "dsOutRacerPrz=dsOutRacerPrz  "
                     + "dsOutRacerAward=dsOutRacerAward  "
                     + "dsOutRacerIjry=dsOutRacerIjry  "
                     + "dsOutRacerSanc=dsOutRacerSanc  "
                     + "dsOutRacerHelmet=dsOutRacerHelmet  ";

	if ( IsExistVar("Arg_sRacerNo") ) {
        fce_AddParam(Trim("RACER_NO     "), Arg_sRacerNo);
        fce_AddParam(Trim("pGBN     "), "002");
        fce_AddParam(Trim("aGBN     "), "001");
        fce_AddParam(Trim("STR_TMS     "), 1);
        fce_AddParam(Trim("END_TMS     "), 100);
    } else {
        fce_AddParam(Trim("RACER_NO     "), edRacerNo.Text);
    }
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false, "S");
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRacer) 
	  && !fnc_DatasetChangeCheck(dsOutRacerFam) 
	  && !fnc_DatasetChangeCheck(dsOutRacerAcad) 
	  && !fnc_DatasetChangeCheck(dsOutRacerRela) 
	  && !fnc_DatasetChangeCheck(dsOutRacerHelmet)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}


	// 필수항목체크
	oDataSet          = dsOutRacer;
	sMandatoryColID   = "RACER_NO|RES_NO|NM_KOR|RACER_STAT_CD|BIRTH_DT|NATV_PLAC_CD";
	sMandatoryColName = "등록번호|주민등록번호|성명(한글)|선수구분|생년월일|출신지";
	oGrid             = "";
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

    // 등록번호 Check
    var sColumnID = "RACER_NO";
    for ( i = 0; i < oDataSet.GetRowCount(); i++ ) {
        if ( oDataSet.GetRowType(i) != "normal" ) {
			// 등록번호 형식이 맞는지 체크한 후 return 한다.
			if ( !fnc_IsNull(oDataSet.GetColumn(i, sColumnID)) ) {
                if (IndexOf(oDataSet.GetColumn(i, sColumnID),"-") < 0){
                    fnc_Message(SNIS_BOA_C001, "N", "등록번호", "-");
                    edRacerEmail.SetFocus();
                    return false;
                }
            }
        }
    }	

    // 주민등록번호 Check
    var sColumnID = "RES_NO";
    for ( i = 0; i < oDataSet.GetRowCount(); i++ ) {
        if ( oDataSet.GetRowType(i) != "normal" ) {
			// 주민번호 형식이 맞는지 체크한 후 return 한다.
			if ( !fnc_ResidenceNumberCheck(oDataSet.GetColumn(i, sColumnID)) ) {
				fnc_Message(SNIS_BOA_0002, "N", i + 1, "주민번호");
			    mskResNo.SetFocus();
				return false;
			}
        }
    }

    var sColumnID = "EMAIL_ADDR";
    for ( i = 0; i < oDataSet.GetRowCount(); i++ ) {
        if ( oDataSet.GetRowType(i) != "normal" ) {
			// 이메일 형식이 맞는지 체크한 후 return 한다.
			if ( !fnc_IsNull(oDataSet.GetColumn(i, sColumnID)) ) {
                if ( !fnc_IsEmail(oDataSet.GetColumn(i, sColumnID)) ) {
                    //fnc_Message(SNIS_BOA_C001, "N", "-", "E-mail");
                    edRacerEmail.SetFocus();
                    return false;
                }
            }
        }
    }

    // 핸드폰번호 Check
    var sColumnID = "CELPON_NO";
    for ( i = 0; i < oDataSet.GetRowCount(); i++ ) {
        if ( oDataSet.GetRowType(i) != "normal" ) {
			// 필드값에 값이 없는 경우 메세지를 호출 한 후 해당 Row번호를 return 한다.
			if ( !fnc_IsNull(oDataSet.GetColumn(i, sColumnID)) ) {
				if (IndexOf(oDataSet.GetColumn(i, sColumnID),"-") < 0){
                    fnc_Message(SNIS_BOA_C001, "N", "핸드폰번호", "-");
                    edCelponNo.SetFocus();
                    return false;
                }
			}
        }
    }

    // 전화번호 Check
    var sColumnID = "HOME_PHONE_NO";
    for ( i = 0; i < oDataSet.GetRowCount(); i++ ) {
        if ( oDataSet.GetRowType(i) != "normal" ) {
			// 필드값에 값이 없는 경우 메세지를 호출 한 후 해당 Row번호를 return 한다.
			if ( !fnc_IsNull(oDataSet.GetColumn(i, sColumnID)) ) {
				if (IndexOf(oDataSet.GetColumn(i, sColumnID),"-") < 0){
                    fnc_Message(SNIS_BOA_C001, "N", "전화번호", "-");
                    edHomePhoneNo.SetFocus();
                    return false;
                }
			}
        }
    }

    for ( i = 0; i < oDataSet.GetRowCount(); i++ ) {
        if ( oDataSet.GetColumn(i, "PTO_UPDATE") == "Y" ) {
			var rtn_arr = fnc_FileWrite(divBtnLst.HttpFile, oDataSet.GetColumn(i, "PTO_PATH") + "\\" + oDataSet.GetColumn(i, "PTO_NM"), 
															oDataSet.GetColumn(i, "PTO_NM"),
															"RACER");

			if ( rtn_arr[0] != "SUCC" ) {
				alert("파일 업로드 에러발생");
				return false;
			}

			var sTemp = Replace(rtn_arr[1],"\\","/");
			var asFile = split(sTemp, "/");

			oDataSet.SetColumn(i, "PTO_PATH", asFile[asFile.length - 1]);
        }
    }

	// 헬멧사진 업로드	
	if (tbDetail2.tab5.rdoHelmetType.Value == "001") {		//기본형인 경우 별도 사진 없음
		dsOutRacerHelmet.SetColumn(0, "PHOTO_FRONT_FILE", "");
		dsOutRacerHelmet.SetColumn(0, "PHOTO_FRONT_FILE_NM", "");			
		dsOutRacerHelmet.SetColumn(0, "PHOTO_BACK_FILE", "");
		dsOutRacerHelmet.SetColumn(0, "PHOTO_BACK_FILE_NM", "");
	}

	if ( dsOutRacerHelmet.GetColumn(0, "PHOTO_FRONT_UPDATE") == "Y" ) {
		var rtn_arr = fnc_FileWrite(divBtnLst.HttpFile, dsOutRacerHelmet.GetColumn(0, "PHOTO_FRONT_FILE") + "\\" + dsOutRacerHelmet.GetColumn(0, "PHOTO_FRONT_FILE_NM"), 
														dsOutRacerHelmet.GetColumn(0, "PHOTO_FRONT_FILE_NM"),	"RACER_HELMET");

		if ( rtn_arr[0] != "SUCC" ) {
			alert("파일 업로드 에러발생");
			return false;
		}

		var sTemp = Replace(rtn_arr[1],"\\","/");
		var asFile = split(sTemp, "/");

		dsOutRacerHelmet.SetColumn(0, "PHOTO_FRONT_FILE", asFile[asFile.length - 1]);
	}
	if ( dsOutRacerHelmet.GetColumn(0, "PHOTO_BACK_UPDATE") == "Y" ) {
		var rtn_arr = fnc_FileWrite(divBtnLst.HttpFile, dsOutRacerHelmet.GetColumn(0, "PHOTO_BACK_FILE") + "\\" + dsOutRacerHelmet.GetColumn(0, "PHOTO_BACK_FILE_NM"), 
														dsOutRacerHelmet.GetColumn(0, "PHOTO_BACK_FILE_NM"),	"RACER_HELMET");

		if ( rtn_arr[0] != "SUCC" ) {
			alert("파일 업로드 에러발생");
			return false;
		}
		var sTemp = Replace(rtn_arr[1],"\\","/");
		var asFile = split(sTemp, "/");

		dsOutRacerHelmet.SetColumn(0, "PHOTO_BACK_FILE", asFile[asFile.length - 1]);
	}


	// 필수항목체크
	oDataSet          = dsOutRacerFam;
	sMandatoryColID   = "RELN_CD|NM";
	sMandatoryColName = "관계|성명";
	oGrid             = "";
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	// 필수항목체크
	oDataSet          = dsOutRacerAcad;
	sMandatoryColID   = "SCHL_TPE_CD|SCHL_NM|GRDU_CD";
	sMandatoryColName = "학력구분|학교명|졸업";
	oGrid             = "";
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	// 필수항목체크
	oDataSet          = dsOutRacerRela;
	sMandatoryColID   = "RELA_RACER_CD|RELA_RACER_NO";
	sMandatoryColName = "선수관계|관계선수번호";
	oGrid             = "";
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

	var sServiceName = "e03010010-service:saveRacerInfo";
    var sInDataSet   = "dsOutRacer=dsOutRacer:u dsOutRacerFam=dsOutRacerFam:u dsOutRacerAcad=dsOutRacerAcad:u dsOutRacerRela=dsOutRacerRela:u dsOutRacerHelmet=dsOutRacerHelmet:u";
    var sOutDataSet  = "dsOutRacer=dsOutRacer dsOutRacerFam=dsOutRacerFam dsOutRacerAcad=dsOutRacerAcad dsOutRacerRela=dsOutRacerRela dsOutRacerHelmet=dsOutRacerHelmet";

	fce_AddParam(Trim("RACER_NO"), edRacerNo.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", "U");
}

