/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
    http.sync = true;
    fcSearchReportData();
	http.sync = false;

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "";               //레포트 파일명
	var sDataSet  = "";   //"UBI 쿼리명, MIP Dataset명"

    if (obj.id == "btnPrtRacerInfo") {
    	sRptNm    = "e03019010_racer_info.jrf";               //레포트 파일명
        sDataSet  = "racer_info=dsOutRacer_rpt racer_fam=dsOutRacerFam_rpt racer_acad=dsOutRacerAcad_rpt racer_rela=dsOutRacerRela_rpt racer_prz=dsOutRacerPrz_rpt racer_award=dsOutRacerAward_rpt";   //"UBI 쿼리명, MIP Dataset명"
    } else if (obj.id == "btnPrtPrize") {
    	sRptNm    = "e03019020_racer_prz.jrf";               //레포트 파일명
        sDataSet  = "racer_prz=dsOutRacerPrz_rpt racer_award=dsOutRacerAward_rpt ";   //"UBI 쿼리명, MIP Dataset명"
    } else if (obj.id == "btnPrtIjry")  {
    	sRptNm    = "e03019030_racer_ijry.jrf";               //레포트 파일명
        sDataSet  = "racer_ijry=dsOutRacerIjry ";   //"UBI 쿼리명, MIP Dataset명"
    } else if (obj.id == "btnPrtSanc")  {
    	sRptNm    = "e03019040_racer_sanc.jrf";               //레포트 파일명
        sDataSet  = "racer_sanc=dsOutRacerSanc ";   //"UBI 쿼리명, MIP Dataset명"
    }     

    //선수사진경로 가져오기
    var sPtoName = dsOutRacer.GetColumn(dsOutRacer_rpt.currow, "PTO_PATH");
    if ( fnc_IsNull(sPtoName) ) return;
    //var sPhysicalFileName = split(sPtoName, "\\");
    var sPhysicalFileName = split(sPtoName, "//");
    var ptourl = GBL_IMAGELINK_URL + sPhysicalFileName[length(sPhysicalFileName) - 1];

	var sArg  = " IMG_URL="+ptourl; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

