function fcSearchReportData(obj) {
	var sServiceName = "e03010010-service:searchRacerInfo_rpt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacer_rpt=dsOutRacer_rpt "
                    + "dsOutRacerFam_rpt=dsOutRacerFam_rpt "
                    + "dsOutRacerAcad_rpt=dsOutRacerAcad_rpt "
                    + "dsOutRacerRela_rpt=dsOutRacerRela_rpt "
                    + "dsOutRacerPrz_rpt=dsOutRacerPrz_rpt "
                    + "dsOutRacerAward_rpt=dsOutRacerAward_rpt "
                    + "dsOutRacerIjry=dsOutRacerIjry "
                    + "dsOutRacerSanc=dsOutRacerSanc ";

	if ( IsExistVar("Arg_sRacerNo") ) {
        fce_AddParam(Trim("RACER_NO     "), Arg_sRacerNo);
