        fce_AddParam(Trim("aGBN     "), "001");
        fce_AddParam(Trim("STR_TMS     "), 1);
        fce_AddParam(Trim("END_TMS     "), 100);
    } else {
        fce_AddParam(Trim("RACER_NO     "), edRacerNo.Text);
    }
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}



