/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 
	switch ( obj.GetForm().id ) {
		case "divBtnRowRacer" :     //선수상세정보		
            fcInit();
		case "divBtnRowRacerTab" : //상단TAB
            if (tbDetail1.TabIndex == 0){        //가족사항
                dsOutRacerFam.AddRow();  
                break;
            }else if (tbDetail1.TabIndex == 1){  //학력사항
                dsOutRacerAcad.AddRow();         //Record Add
                break;
            }else if (tbDetail1.TabIndex == 2){  //선수관계
                dsOutRacerRela.AddRow();         //Record Add
                break;
            }
     }
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	switch ( obj.GetForm().id ) {
		case "divBtnRowRacer" :
			if ( !fnc_DeleteCheck(dsOutRacer, "chk", "1") ) return;
            fcInit();
			grdRacer.SetFocus();                                  //Record 삭제 후 Grid로 Focus
			break;			
		case "divBtnRowRacerTab" :
            if (tbDetail1.TabIndex == 0){        //가족사항
                if ( !fnc_DeleteCheck(dsOutRacerFam, "chk", "1") ) return;
                grdOutRacerFam.SetFocus();
                break;                
            }else if (tbDetail1.TabIndex == 1){  //학력사항
                if ( !fnc_DeleteCheck(dsOutRacerAcad, "chk", "1") ) return;
                grdOutRacerAcad.SetFocus();
                break;                
            }else if (tbDetail1.TabIndex == 2){  //선수관계
                if ( !fnc_DeleteCheck(dsOutRacerRela, "chk", "1") ) return;
                grdOutRacerRela.SetFocus();
                break;
            }
	}
}

