/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);	
}

/*-------------------------------------------------------------------------------------------------+
>>  Help Popup Open
+-------------------------------------------------------------------------------------------------*/
function fcHelpDialoge(obj,nX,nY) {

	var sFormName   = "";    //호출할 Form ID
	var sInputValue = "";    //입력된 자료가 있다면 Default로 조회 
    var sResponse;           //Popup에서 받을 결과 값

	switch(obj.id) {
		case "imgPost" :   //우편번호 조회
			sSearchKind = "POST";                    //우편번호 조회 POPUP
			sInputValue = "";                        //검색 조건 Text
			sCommonGubn = "";                        //공통 구분 코드(공통코드 조회가 아니면 null)
			sResponse   = fce_HelpDialoge(sSearchKind,sInputValue,"");  // Popup Open

			if ( sResponse != 0 ) {
				mskPostNo.value = sResponse[0];    //결과 값 Display(Select 컬럼 순서대로 0,1,2..)
				edHomeAdd1.Text = sResponse[1];    //결과 값 Display(Select 컬럼 순서대로 0,1,2..)
			}
			break;

		case "imgPhoto" :   //사진
			sResponse = fce_GetSelectFile();

			if ( !fnc_IsNull(sResponse) ) {
				dsOutRacer.SetColumn(dsOutRacer.currow, "PTO_UPDATE", "Y"      );
				dsOutRacer.SetColumn(dsOutRacer.currow, "PTO_PATH"  , sResponse[0]);
				dsOutRacer.SetColumn(dsOutRacer.currow, "PTO_NM"    , sResponse[1]);

				edPtoNm.Text = sResponse[0] + "\\" + sResponse[1];
				imgPhotoNm.ImageID = edPtoNm.Text;
				imgPhotoNm.Text = "";
				imgPhotoNm.FillType = "STRETCH";	
			}
			break;	


		case "ImgHelmetFront" :   // 헬멧 전측면 사진
		case "ImgHelmetBack"  :   // 헬멧 후측면 사진
			if (tbDetail2.tab5.rdoHelmetType.Value == "001") return;
			else tbDetail2.tab5.rdoHelmetType.Value = "002";
			sResponse = fce_GetSelectFile();

			if ( !fnc_IsNull(sResponse) ) {
				if (obj.id == "ImgHelmetFront") {
					dsOutRacerHelmet.SetColumn(dsOutRacerHelmet.currow, "PHOTO_FRONT_UPDATE", "Y");
					dsOutRacerHelmet.SetColumn(dsOutRacerHelmet.currow, "PHOTO_FRONT_FILE" , 	sResponse[0]); // 경로. 추후 업로드시 실제 파일명으로 UPDATE
					dsOutRacerHelmet.SetColumn(dsOutRacerHelmet.currow, "PHOTO_FRONT_FILE_NM" , sResponse[1]);
					tbdetail2.tab5.ImgHelmetFront.ImageID = sResponse[0] + "\\" + sResponse[1];
					tbdetail2.tab5.ImgHelmetFront.FillType = "STRETCH";	
				} else {
					dsOutRacerHelmet.SetColumn(dsOutRacerHelmet.currow, "PHOTO_BACK_UPDATE", "Y");
					dsOutRacerHelmet.SetColumn(dsOutRacerHelmet.currow, "PHOTO_BACK_FILE" , sResponse[0]);
					dsOutRacerHelmet.SetColumn(dsOutRacerHelmet.currow, "PHOTO_BACK_FILE_NM" , sResponse[1]);
					tbdetail2.tab5.ImgHelmetBack.ImageID = sResponse[0] + "\\" + sResponse[1];
					tbdetail2.tab5.ImgHelmetBack.FillType = "STRETCH";	
				}
			}
			break;	
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  그리드에서 팝업 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnExpandEdit(objGrd, nRow, nCell, strVal, nPivotIndex){

	var sColIdx     = objGrd.GetCellText("HEAD", nRow, nCell);  //해당 HEAD명 
	var sSearchKind = "";
	var sInputValue = "";
    var sResponse;                                              //Popup에서 받을 결과 값

	switch (sColIdx){
		case "생년월일" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;

		case "입학일자" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;			

		case "졸업일자" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;	
		case "성명" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;			

		default :
			break;
	}
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
	    // 종교에 '전체' 항목 추가
		fce_AddCodeRow(dsOutRelgn, "전체", "", "");
		fce_AddCodeRow(dsOutArmyClas, "전체", "", "");
		fce_AddCodeRow(dsOutArmyTpe, "전체", "", "");

        if ( IsExistVar("Arg_sRacerNo") ) {
            edRacerNo.Readonly = true;
            fcSearch();     
        } else {
            fcAddRacer();
        }        

    }

	// 조회 후
	if ( sServiceName == "e03010010-service:searchRacerDetl" ) { 
        if ( dsOutRacer.GetRowCount() == 0 ) {
            dsOutRacer.AddRow();
        }        
        dsOutRacer.AddColumn("PTO_UPDATE");
        dsOutRacerFam.AddColumn("chk");
        dsOutRacerAcad.AddColumn("chk");
        dsOutRacerRela.AddColumn("chk");
        if (dsOutRacerHelmet.rowcount() == 0) dsOutRacerHelmet.AddRow();

        fnc_SetStatus(SNIS_BOA_0003, dsOutRacer.GetRowCount());
	}

	// 조회 후
	if ( sServiceName == "e03010010-service:searchRacer" ) { 
        return;
	}

	// 저장 후
	if ( sServiceName == "e03010010-service:saveRacerInfo" ) {
        bSave = true;
        edRacerNo.Readonly = true;
        fnc_SetBKColor(this);
        fcSearch();
        dsOutRacer_OnRowPosChanged(dsOutRacer,0,0); 
        return;
	}

	// 삭제 후
	if ( sServiceName == "e03010010-service:deleteRacerInfo" ) {
        bSave = true;
        fcEnd(bSave);
        return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

