function fcAddRacer(obj)
{
	fcInit();
    //신규등록시에는 선수를 신인으로 세팅
    dsOutRacer.SetColumn(0,"RACER_STAT_CD",'002');
    //신규등록시에는 선수를 B2등급으로 세팅
    dsOutRacer.SetColumn(0,"RACER_GRD_CD",'B2');	
}

/*-------------------------------------------------------------------------------------------------+
>>  선택 사원이 변경된 경우 해당 사원의 사진을 보여준다.
+-------------------------------------------------------------------------------------------------*/
function dsOutRacer_OnRowPosChanged(obj,nOldRow,nRow)
{
    this.imgPhotoNm.ImageID = "";
    var sPtoName = dsOutRacer.GetColumn(nRow, "PTO_PATH");

    if ( fnc_IsNull(sPtoName) ) return;

    //var sPhysicalFileName = split(sPtoName, "\\");
    var sPhysicalFileName = split(sPtoName, "//");
    var url = GBL_IMAGELINK_URL + sPhysicalFileName[length(sPhysicalFileName) - 1];

	this.imgPhotoNm.ImageID = url;
	imgPhotoNm.FillType = "STRETCH";	
}


function dsOutRacerRela_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID != "RELA_RACER_NM") return;

    var sRacerNo  = dsOutRacerRela.GetColumn(nRow, "RELA_RACER_NO");
    var sRacerNm  = dsOutRacerRela.GetColumn(nRow, "RELA_RACER_NM");

    if ( fnc_IsNull(sRacerNm) ) return;

    //등록번호가 입력안되었을때는 성명을 가지고 등록번호를 search 
    var sServiceName = "e03010010-service:searchRacer";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutSRacer=dsOutSRacer";

    fce_AddParam(Trim("NM_KOR       "), sRacerNm);

    http.sync = true;
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "", false);
    http.sync = false;

    var sRelaRacerNo = dsOutSRacer.GetColumn(dsOutSRacer.currow, "RACER_NO");
    var sRelaRacerNm = dsOutSRacer.GetColumn(dsOutSRacer.currow, "NM_KOR");
    //검색결과가 없거나 1건 이상이라면 선수검색창을 보여줌.
    if ( dsOutSRacer.GetRowCount() == 1 ) {
        dsOutRacerRela.SetColumn(nRow, "RELA_RACER_NO"  , sRelaRacerNo);
        dsOutRacerRela.SetColumn(nRow, "RELA_RACER_NM"  , sRelaRacerNm);
    }else{
        dsOutRacerRela.SetColumn(nRow, "RELA_RACER_NO"  , "");

        // Content로 넘겨지는 arguements
        var sArg = "Arg_sRacerNm=" + quote(sRacerNm);
        var sResponse = Dialog("form::E03010012.xml", sArg, 834, 450, "");

        if ( !fnc_IsNull(sResponse) ) {
            dsOutRacerRela.SetColumn(nRow, "RELA_RACER_NO"  , sResponse[0]);
            dsOutRacerRela.SetColumn(nRow, "RELA_RACER_NM"  , sResponse[1]);
        }
    }                
}


function dsOutRacerHelmet_OnRowPosChanged(obj,nOldRow,nRow)
{
	// 전측면 이미지
    tbDetail2.tab5.ImgHelmetFront.ImageID = "";
    var sPtoName = dsOutRacerHelmet.GetColumn(nRow, "PHOTO_FRONT_FILE");

    if ( fnc_IsNull(sPtoName) ) return;

    //var sPhysicalFileName = split(sPtoName, "\\");
    var sPhysicalFileName = split(sPtoName, "//");
    var url = GBL_IMAGELINK_URL_HELMET + sPhysicalFileName[length(sPhysicalFileName) - 1];

	tbDetail2.tab5.ImgHelmetFront.ImageID = url;
	tbDetail2.tab5.ImgHelmetFront.FillType = "STRETCH";	

	// 후측면 이미지
    tbDetail2.tab5.ImgHelmetBack.ImageID = "";
    var sPtoName = dsOutRacerHelmet.GetColumn(nRow, "PHOTO_BACK_FILE");

    if ( fnc_IsNull(sPtoName) ) return;

    //var sPhysicalFileName = split(sPtoName, "\\");
    var sPhysicalFileName = split(sPtoName, "//");
    var url = GBL_IMAGELINK_URL_HELMET + sPhysicalFileName[length(sPhysicalFileName) - 1];
	tbDetail2.tab5.ImgHelmetBack.ImageID = url;
	tbDetail2.tab5.ImgHelmetBack.FillType = "STRETCH";	
}

function fcRdoOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (obj.id == "rdoHelmetType") {
		if (strCode == "001") {
			tbDetail2.tab5.ImgHelmetFront.ImageID = GBL_IMAGELINK_URL_HELMET + dsHelmetDefault.GetColumn(0, "CD_NM");
			tbDetail2.tab5.ImgHelmetBack.ImageID  = GBL_IMAGELINK_URL_HELMET + dsHelmetDefault.GetColumn(1, "CD_NM");
		} else {
			tbDetail2.tab5.ImgHelmetFront.ImageID = "";
			tbDetail2.tab5.ImgHelmetBack.ImageID  = "";
		}
	}
}

//선수 심층 면담자료버튼 클릭이벤트
function btnRacerRef_OnClick(obj)
{
	var sRacerNo = dsOutRacer.GetColumn(0, "RACER_NO");
	var sRacerNm = dsOutRacer.GetColumn(0, "NM_KOR");
	var sArg  = "Arg_sRacerNo=" + quote(sRacerNo);
		sArg += " Arg_sRacerNm=" + quote(sRacerNm); 
		sArg += " Arg_sImgPhotoNm=" + quote(imgPhotoNm.ImageID);

	if ( fnc_IsNull(sRacerNo) || fnc_IsNull(sRacerNm) || fnc_IsNull(imgPhotoNm.ImageID)) {
		return;
	}

//    Dialog("form::E02080310.xml", sArg, 1024, 752 , "", 400, 250);
    Dialog("form::E02080310.xml", sArg, 1024, 752 , "");
}
