/************************
*  공통 Script Include  *
************************/
#include "lib::boa_common_script.js";
#include "lib::boa_app_script.js";


/*********************
*  화면 변수 선언부  *
*********************/
var sFormId       = 'E03010013';

var sArg_Gubun    = '';
var sArg_imgNm    = '';

/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){
	sArg_Gubun = Arg_Gubun;
	sArg_imgNm = Arg_imgNm;

	fcSearch();
}

/*-----------------------+
|  이미지 크게 보기 !  |
+------------------------*/
function fcSearch(obj) {	
	var url = "";
    this.imgNm.ImageID = "";

    if ( !fnc_IsNull(sArg_imgNm) ) {
    	var sPhysicalFileName = split(sArg_imgNm, "//");
		switch(sArg_Gubun) {
			case "Racer" :   //선수
				url = GBL_IMAGELINK_URL + sPhysicalFileName[length(sPhysicalFileName) - 1];
				break;
			case "Fair" :   //공정
				url = GBL_IMAGELINK_URL_FAIRNESS + sPhysicalFileName[length(sPhysicalFileName) - 1];
				break;
		}

		this.imgNm.ImageID = url;
		imgNm.FillType = "STRETCH";
	}
}

function fcEnd(obj) {
	Close(); 
}
