/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj)
{
    sBUTTONLIST   = 'T'+sInpt+'FTFT';

	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

    //년도조회
    fce_SetStndYear(dsOutSStndYear, toInteger(fce_GetSysdate("YYYY")));
    cboSStndYear.Index = 0;

	//공통코드 조회
	fce_GetComboDs(dsInComCd);

	// 화면 초기화
	fcInit();
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam)
{
	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj)
{
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsOutRacerTilt";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

    for ( var i = 1; i <= dsOutRacerTilt.GetRowCount(); i++ ) {
        Destroy("rdoTilt"   + i);
        Destroy("rdoJacket" + i);
        Destroy("rdoBoat"   + i);
    }

    dsOutRacerTilt.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;
    grdmaster.Redraw = false;

	var sServiceName = "e03030020-service:searchRacerTilt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerTilt=dsOutRacerTilt  ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD"), cboSMbr.Value);
	fce_AddParam(Trim("TMS"), cboSRaceTms.Value);
	fce_AddParam(Trim("DAY_ORD"), cboSRaceDayOrd.Value);
	fce_AddParam(Trim("RACE_NO"), cboSRaceNo.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

    for ( var i = 1; i <= dsOutRacerTilt.GetRowCount(); i++ ) {
        dsOutRacerTilt.SetColumn(i - 1, "TILT_CD"           , object("rdoTilt"   + i).Value);
        dsOutRacerTilt.SetColumn(i - 1, "JACKET_ADD_WGHT_CD", object("rdoJacket" + i).Value);
        dsOutRacerTilt.SetColumn(i - 1, "BOAT_ADD_WGHT_CD"  , object("rdoBoat"   + i).Value);
    }

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRacerTilt       ) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutRacerTilt;
	sMandatoryColID   = "RACER_NO||NM_KOR||TILT_CD||JACKET_ADD_WGHT_CD||BOAT_ADD_WGHT_CD";
	sMandatoryColName = "등록번호||선수명||조정각||조끼부과량||보트부과량";
	oGrid             = grdMaster;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e03030020-service:SaveRacerTilt";
    var sInDataSet   = "dsOutRacerTilt=dsOutRacerTilt:u ";
    var sOutDataSet  = "dsOutRacerTilt=dsOutRacerTilt ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD"), cboSMbr.Value);
	fce_AddParam(Trim("TMS"), cboSRaceTms.Value);
	fce_AddParam(Trim("DAY_ORD"), cboSRaceDayOrd.Value);
	fce_AddParam(Trim("RACE_NO"), cboSRaceNo.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

