/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;
        cboSMbr.Value = CURR_USER_MBR_CD;

        fce_GetRaceTms(cboSStndYear.Value,cboSMbr.Value,"","dsOutSRaceTms");
		return;
	}

	// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
		dsOutSMaxTms.Filter("STND_YEAR='"+cboSStndYear.Value+"'&&MBR_CD='"+cboSMbr.Value+"'");
		var maxTms = dsOutSMaxTms.GetColumn(0,"MAX_TMS");
		dsOutSRaceTms.Filter("TMS<=" + maxTms);

        cboSRaceTms.Index = 0;
        if ( cboSStndYear.Value == CURR_STND_YEAR ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", CURR_TMS);
            if ( nIndexRow > -1 ) cboSRaceTms.Index = nIndexRow;
        }       
        fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSRaceDayOrd");
		return;
	}

	// 일차 리스트 조회 후
	if ( sServiceName == "common_race-service:searchRaceDayOrd" ) {
        cboSRaceDayOrd.Index = 0;

        if ( cboSRaceTms.Value == CURR_TMS ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceDayOrd, "DAY_ORD", CURR_DAY_ORD);

            if ( nIndexRow > -1 ) cboSRaceDayOrd.Index = nIndexRow;
        }

        fce_GetRace(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, cboSRaceDayOrd.Value, "dsOutSRaceNo");
		return;
	}

	// 경주 리스트 조회 후
	if ( sServiceName == "common_race-service:searchRace" ) {
        cboSRaceNo.Index = 0;
        /*if ( cboSRaceDayOrd.Value == CURR_DAY_ORD ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceNo, "RACE_NO", CURR_RACE_NO );

            if ( nIndexRow > -1 ) cboSRaceNo.Index = nIndexRow;
        }*/

        fcSearch();
		return;
	}

	// 조회 후
	if ( sServiceName == "e03030020-service:searchRacerTilt" ) {
        fcCreateRadio();
        grdMaster.Redraw = true;

        if ( cboSRaceDayOrd.Value == CURR_DAY_ORD ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceNo, "RACE_NO", CURR_RACE_NO );

            if ( nIndexRow > -1 ) cboSRaceNo.Index = nIndexRow;
        }		

        if ( dsOutRacerTilt.GetRowCount() == 0 ) {
        }
        fnc_SetStatus(SNIS_BOA_0003, dsOutRacerTilt.GetRowCount());

		return;
	}

	// 저장 후
	if ( sServiceName == "e03030020-service:SaveRacerTilt" ) {
        //fcSearch();
		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

