/*-------------------------------------------------------------------------------------------------+
>>  년도, 분기 변경시
+-------------------------------------------------------------------------------------------------*/
function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcInit();
    fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");
}

/*-------------------------------------------------------------------------------------------------+
>>  회차변경시
+-------------------------------------------------------------------------------------------------*/
function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var nRow = fnc_GetExistValue(dsOutSRaceTms, "Tms", cboSRaceTms.Value);
    fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSRaceDayOrd");
}

/*-------------------------------------------------------------------------------------------------+
>>  일차 변경시
+-------------------------------------------------------------------------------------------------*/
function cboSRaceDayOrd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var nRow = fnc_GetExistValue(dsOutSRaceDayOrd, "DAY_ORD", cboSRaceDayOrd.Value);
    fce_GetRace(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, cboSRaceDayOrd.Value, "dsOutSRaceNo");
}

/*-------------------------------------------------------------------------------------------------+
>>  경주 변경시
+-------------------------------------------------------------------------------------------------*/
function cboSRaceNo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var nRow = fnc_GetExistValue(dsOutSRaceNo, "RACE_NO", cboSRaceNo.Value);
    fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  경주 변경시
+-------------------------------------------------------------------------------------------------*/
function fcCreateRadio()
{
    var nTop = 90;

    for ( var i = 1; i <= dsOutRacerTilt.GetRowCount(); i++ ) {

        nTop = nTop + 60;
        var sRadioType = "";
        sRadioType += ' Border="None"            ';
        sRadioType += ' CodeColumn="CD"          ';
        sRadioType += ' ColumnCount="5"          ';
        sRadioType += ' DataColumn="CD_NM"       ';
        sRadioType += ' Height="58"              ';
        sRadioType += ' Id="rdoTilt' + i + '"    ';
        sRadioType += ' INDEX="1"                ';
        sRadioType += ' InnerDataset="dsOutTilt" ';
        sRadioType += ' Left="160"               ';
        sRadioType += ' LeftMargin="5"           ';
        sRadioType += ' RightMargin="0"          ';
        sRadioType += ' Style="snis_m_radio"     ';
        sRadioType += ' Top="' + (nTop -10) + '"       ';
        sRadioType += ' Width="303"              ';

        Create("Radio","rdoTilt" + i, sRadioType);
        object("rdoTilt" + i).Value = dsOutRacerTilt.GetColumn(i - 1, "TILT_CD");
//Radio의 색상을 코드의 RMK 값으로 세팅  ->  SetRadioProp의 " Color" property가 없네... 
//        eval("rdoTilt" + i).SetRadioProp(i - 1, "Color", dsOutTilt.GetColumn(i - 1, "RMK");

        var sRadioType = "";
        sRadioType += ' Border="None"            ';
        sRadioType += ' CodeColumn="CD"          ';
        sRadioType += ' ColumnCount="10"          ';
        sRadioType += ' DataColumn="CD_NM"       ';
        sRadioType += ' Height="29"              ';
        sRadioType += ' Id="rdoJacket' + i + '"  ';
        sRadioType += ' INDEX="1"                ';
        sRadioType += ' InnerDataset="dsOutJacketAdd" ';
        sRadioType += ' Left="467"               ';
        sRadioType += ' LeftMargin="5"           ';
        sRadioType += ' RightMargin="0"          ';
        sRadioType += ' Style="snis_m_radio"     ';
        sRadioType += ' Top="' + (nTop - 10) + '"       ';
        sRadioType += ' Width="502"              ';
        sRadioType += ' BkColor="antiquewhite"      ';

        Create("Radio","rdoJacket" + i, sRadioType);
        object("rdoJacket" + i).Value = dsOutRacerTilt.GetColumn(i - 1, "JACKET_ADD_WGHT_CD");

        var sRadioType = "";
        sRadioType += ' Border="None"            ';
        sRadioType += ' CodeColumn="CD"          ';
        sRadioType += ' ColumnCount="10"          ';
        sRadioType += ' DataColumn="CD_NM"       ';
        sRadioType += ' Height="29"              ';
        sRadioType += ' Id="rdoBoat' + i + '"    ';
        sRadioType += ' INDEX="1"                ';
        sRadioType += ' InnerDataset="dsOutBoatAdd" ';
        sRadioType += ' Left="467"               ';
        sRadioType += ' LeftMargin="5"           ';
        sRadioType += ' RightMargin="0"          ';
        sRadioType += ' Style="snis_m_radio"     ';
        sRadioType += ' Top="' + (nTop + 17) + '"       ';
        sRadioType += ' Width="502"              ';

        Create("Radio","rdoBoat" + i, sRadioType);
        object("rdoBoat" + i).Value = dsOutRacerTilt.GetColumn(i - 1, "BOAT_ADD_WGHT_CD");
    }
}
