/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutRacerArrangeEsc.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

    if ( !fnc_IsMandatory(calSStrDt  , "시작일") ) return false;
    if ( !fnc_IsMandatory(calSEndDt  , "종료일") ) return false;

    if ( calSStrDt.Value > calSEndDt.Value)
    {
        fnc_Message(SNIS_BOA_E001, "N");
        return false;
    }

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e03060015-service:searchRacerArrangeEsc";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerArrangeEsc=dsOutRacerArrangeEsc  ";

	fce_AddParam(Trim("STR_DT"), calSStrDt.Value);
	fce_AddParam(Trim("END_DT"), calSEndDt.Value);
	fce_AddParam(Trim("NM_KOR"), edRacerNm.Text);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchRecdStndTermCheck(obj) {
	fcInit(obj);

	return true;
}

function fcSearchRecdStndTerm(obj) {
    if ( !fcSearchRecdStndTermCheck(obj) ) return;

	var sServiceName = "e02010040-service:searchRecdStndTerm";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRecdStndTerm=dsOutRecdStndTerm ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchPrintCheck(obj) {

    if ( !fnc_IsMandatory(calSStrDt  , "시작일") ) return false;
    if ( !fnc_IsMandatory(calSEndDt  , "종료일") ) return false;

    if ( calSStrDt.Value > calSEndDt.Value)
    {
        fnc_Message(SNIS_BOA_E001, "N");
        return false;
    }

	return true;
}

function fcSearchPrint(obj) {
    if ( !fcSearchPrintCheck(obj) ) return;

	var sServiceName = "e03060015-service:searchRacerArrangeEscPrint";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerSummery=dsOutRacerSummery "
                     + "dsOutRacerCancel=dsOutRacerCancel "
                     + "dsOutRacerEsc=dsOutRacerEsc "
                     + "dsOutRacerEtc=dsOutRacerEtc ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("QURT_CD  "), cboSQurt.Value    );
	fce_AddParam(Trim("STR_DT   "), calSStrDt.Value   );
	fce_AddParam(Trim("END_DT   "), calSEndDt.Value   );
	fce_AddParam(Trim("NM_KOR   "), edRacerNm.Text    );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRacerArrangeEsc       ) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutRacerArrangeEsc;
	sMandatoryColID   = "RACER_NO|NM_KOR|RSN_CD|STR_DT|END_DT";
	sMandatoryColName = "등록번호|선수명|주선보류분류|주선보류시작일|주선보류종료일";
	oGrid             = grdRacerList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
    if ( divEscRacer.calArrangeEscSdt.Value > divEscRacer.calArrangeEscEdt.Value )
    {
        fnc_Message(SNIS_BOA_E001, "N");
        return false;
    }

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e03060015-service:saveRacerArrangeEsc";
    var sInDataSet   = "dsOutRacerArrangeEsc=dsOutRacerArrangeEsc:u ";
    var sOutDataSet  = "dsOutRacerArrangeEsc=dsOutRacerArrangeEsc ";

	fce_AddParam(Trim("STR_DT"), calSStrDt.Value);
	fce_AddParam(Trim("END_DT"), calSEndDt.Value);
	fce_AddParam(Trim("NM_KOR"), edRacerNm.Text);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

