/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
    dsOutRacerArrangeEsc.AddRow();         //Record Add
    divEscRacer.edRacerNm.SetFocus();
    dsOutArrangeEsc.UnFilter();
    dsOutArrangeEsc.Filter("CD='002'||CD='003'||CD='004'||CD='101'");
    divEscRacer.cboArrangeEsc.Enable = true;
    divEscRacer.cboArrangeEsc.Index  = 0;
    break;
}


