/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회 완료 후
	if ( sServiceName == "common-service:searchCode" ) {                        
		fcSearchRecdStndTerm();
		cboSQurt.Index = 0;

        return;
	}

	// 조회 후
	if ( sServiceName == "e02010040-service:searchRecdStndTerm" ) {
        cboSQurt_OnChanged();
        return;
    }

	// 조회 후
	if ( sServiceName == "e03060015-service:searchRacerArrangeEsc" ) {
        dsOutRacerArrangeEsc.AddColumn("chk");
        fnc_SetStatus(SNIS_BOA_0003, dsOutRacerArrangeEsc.GetRowCount());
		return;
	}

	// 조회 후
	if ( sServiceName == "e03060015-service:searchRacerArrangeEscPrint" ) {

        dsOutYear.ClearData();
        dsOutYear.AddColumn("STND_YEAR");
        dsOutYear.AddColumn("QURT_NM"  );
        dsOutYear.AddColumn("STR_DT"   );
        dsOutYear.AddColumn("END_DT"   );
        dsOutYear.AddColumn("ESC_CNT"  );

        dsOutYear.AddRow();
        dsOutYear.SetColumn(0, "STND_YEAR", cboSStndYear.Value);
        dsOutYear.SetColumn(0, "QURT_NM"  , cboSQurt.Value    );
        dsOutYear.SetColumn(0, "STR_DT"   , calSStrDt.Value   );
        dsOutYear.SetColumn(0, "END_DT"   , calSEndDt.Value   );
        dsOutYear.SetColumn(0, "ESC_CNT"  , dsOutRacerEsc.GetRowCount() + dsOutRacerEtc.GetRowCount());

        var logImgUrl = GBL_REPORT_URL + "log.jpg";
        var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
        var sRptNm    = "e03069015_esc_racer.jrf";               //레포트 파일명
        var sDataSet  = "year=dsOutYear summery_racer=dsOutRacerSummery cancel_racer=dsOutRacerCancel esc_racer=dsOutRacerEsc etc_racer=dsOutRacerEtc";   //"UBI 쿼리명, MIP Dataset명"
        var sArg      = " IMG_URL="+logImgUrl
                      + " STND_YEAR="+cboSStndYear.Value
                      + " QURT_NM="+cboSQurt.Text;

        var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

        fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);

        return;
	}

	// 저장 후
	if ( sServiceName == "e03060015-service:saveRacerArrangeEsc" ) {
        dsOutRacerArrangeEsc.AddColumn("chk");
        fnc_SetStatus(SNIS_BOA_0003, dsOutRacerArrangeEsc.GetRowCount());
		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

