function dsOutRacerArrangeEsc_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID != "NM_KOR") return;

    var sRacerNo  = dsOutRacerArrangeEsc.GetColumn(nRow, "RACER_NO");
    var sRacerNm  = dsOutRacerArrangeEsc.GetColumn(nRow, "NM_KOR");

    if ( fnc_IsNull(sRacerNm) ) return;

    //등록번호가 입력안되었을때는 성명을 가지고 등록번호를 search     
    var sServiceName = "e03010010-service:searchRacer";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutSRacer=dsOutSRacer";

    fce_AddParam(Trim("NM_KOR       "), sRacerNm);

    http.sync = true;
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "", false);
    http.sync = false;

    var sRacerNo = dsOutSRacer.GetColumn(dsOutSRacer.currow, "RACER_NO");
    var sRacerNm = dsOutSRacer.GetColumn(dsOutSRacer.currow, "NM_KOR");
    //검색결과가 없거나 1건 이상이라면 선수검색창을 보여줌.
    if ( dsOutSRacer.GetRowCount() == 1 ) {
        dsOutRacerArrangeEsc.SetColumn(nRow, "RACER_NO"  , sRacerNo);
        dsOutRacerArrangeEsc.SetColumn(nRow, "NM_KOR"  , sRacerNm);
    }else{
        dsOutRacerArrangeEsc.SetColumn(nRow, "RACER_NO"  , "");

        // Content로 넘겨지는 arguements
        var sArg = "Arg_sRacerNm=" + quote(sRacerNm);
        var sResponse = Dialog("form::E03010012.xml", sArg, 834, 450, "");

        if ( !fnc_IsNull(sResponse) ) {
            dsOutRacerArrangeEsc.SetColumn(nRow, "RACER_NO"  , sResponse[0]);
            dsOutRacerArrangeEsc.SetColumn(nRow, "NM_KOR"  , sResponse[1]);
        }
    }                
}

function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearchRecdStndTerm();
}

function cboSQurt_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    dsOutRecdStndTerm.row = fnc_GetExistValue(dsOutRecdStndTerm, "QURT_CD", cboSQurt.Value);
}

function dsOutRacerArrangeEsc_OnRowPosChanged(obj,nOldRow,nRow)
{
    var sRsnCd = dsOutRacerArrangeEsc.GetColumn(nRow, "RSN_CD");
    if ( sRsnCd == '002' || sRsnCd == '003' || sRsnCd == '004' || sRsnCd == '101' ) {
        dsOutArrangeEsc.UnFilter();
		dsOutArrangeEsc.Filter("CD='002'||CD='003'||CD='004'||CD='101'");
        divEscRacer.cboArrangeEsc.Enable = true;
    } else {
        dsOutArrangeEsc.UnFilter();
        divEscRacer.cboArrangeEsc.Enable = false;
    }
}
